	[534] = {id = 534,name = "稻田里的守护者",fit_gun = 143,voice = 4,substitute_voice = "",dialog = "虽然这是一件十分讨厌的事，但如果能护你周全，我愿意二十四小时待命。",note = "",illustrator_cv = {"影法师", "福原綾香"},class = 59,type = 0},
