	[536] = {id = 536,name = "偿愿的礼花魔法师",fit_gun = 101,voice = 4,substitute_voice = "",dialog = "当当当——魔法师的偿愿事务所开张啦！如果你有什么想要实现的心愿的话，请排在45姐的后面！",note = "",illustrator_cv = {"影法师", "能登麻美子"},class = 59,type = 0},
	[537] = {id = 537,name = "千宵草味的锡纸糖",fit_gun = 65,voice = 4,substitute_voice = "",dialog = "簌簌簌——当你吃下这颗糖，你也就和我有了约定，不好好遵守的话我是不会轻易放过你的。",note = "",illustrator_cv = {"影法师", "野中藍"},class = 59,type = 0},
	[538] = {id = 538,name = "寻找勇气的雷克斯兔",fit_gun = 122,voice = 4,substitute_voice = "",dialog = "叽叽叽——什么，兔子是没有声带的吗？那下次有任务找我的时候还是直接装睡好了……",note = "",illustrator_cv = {"影法师", "福原綾香"},class = 59,type = 0},
	[539] = {id = 539,name = "守望心愿的暗影女巫",fit_gun = 102,voice = 4,substitute_voice = "",dialog = "砰砰砰——寄存在我这里的心愿，正在无人知晓的土壤中悄悄发芽……",note = "",illustrator_cv = {"影法师", "上間江望"},class = 59,type = 0},
	[540] = {id = 540,name = "极昼微光",fit_gun = 206,voice = 4,substitute_voice = "",dialog = "指挥官，你的体温比平时高了0.2℃，心率也在加快，我看起来很让人害怕吗？……闭上眼？这次不行。",note = "",illustrator_cv = "",class = 0,type = 0},
	[541] = {id = 541,name = "暗影行动",fit_gun = 1025,voice = 4,substitute_voice = "",dialog = "只有任性的大人才会选择耍帅独行。我已经准备万全，随时可以和你一起出发。",note = "",illustrator_cv = "",class = 53,type = 0},
	[542] = {id = 542,name = "自由餐厅",fit_gun = 1024,voice = 4,substitute_voice = "",dialog = "今天我是作为女仆来这里的，要听我拉琴的话，得等下次了哦。",note = "",illustrator_cv = "",class = 53,type = 0},
	[543] = {id = 543,name = "童话起点",fit_gun = 1027,voice = 4,substitute_voice = "",dialog = "总觉得很久之前也这样在出门前精心准备呢……小皮？！你是从哪里跑出来的？",note = "",illustrator_cv = "",class = 53,type = 0},
	[544] = {id = 544,name = "月神假日",fit_gun = 1023,voice = 4,substitute_voice = "",dialog = "我们要快一点了，今晚的海边可以看到射手座呢。",note = "",illustrator_cv = "",class = 53,type = 0},
	[545] = {id = 545,name = "晨风庭园",fit_gun = 1026,voice = 4,substitute_voice = "",dialog = "早安，欢迎来到我的庭院。曾经有人把它交给了我，现在它也属于你了。",note = "",illustrator_cv = "",class = 53,type = 0},
	[546] = {id = 546,name = "曼岛之盾",fit_gun = 1029,voice = 4,substitute_voice = "",dialog = "新的麻烦又来了吗……真是的，我的城市可装不下那么多乱七八糟的货色。",note = "",illustrator_cv = "",class = 53,type = 0},
	[547] = {id = 547,name = "浴火凛锋",fit_gun = 1028,voice = 4,substitute_voice = "",dialog = "看来这次的工作是去拯救一个大家伙……",note = "",illustrator_cv = "",class = 53,type = 0},
	[548] = {id = 548,name = "獠牙",fit_gun = 65,voice = 4,substitute_voice = "",dialog = "自由与平等并非与生俱来的权力，你能够选择的，只有服从或是死亡。",note = "",illustrator_cv = "",class = 53,type = 0},
	[549] = {id = 549,name = "地狱火",fit_gun = 20,voice = 4,substitute_voice = "",dialog = "若要净化城市，难免得付出一点点代价……",note = "",illustrator_cv = {"RIRE", "早見沙織"},class = 53,type = 0},
	[550] = {id = 550,name = "松间曦光",fit_gun = 55,voice = 4,substitute_voice = "",dialog = "大家像这样一起为一件事努力，是我最喜欢的……诶，到我了吗？来、来了",note = "",illustrator_cv = {"暖森", "戸松遥"},class = 60,type = 0},
	[551] = {id = 551,name = "深林鸣音",fit_gun = 56,voice = 4,substitute_voice = "",dialog = "哇，这个吹起来不错，敲起来也很好听！这样用指甲在上面叮叮当当……不可以吗？为什么？",note = "",illustrator_cv = {"暖森", "田村ゆかり"},class = 60,type = 1},
	[552] = {id = 552,name = "春日幻风",fit_gun = 57,voice = 4,substitute_voice = "",dialog = "这种指法不算复杂，我没问题。随时都可以开始，你们呢？",note = "",illustrator_cv = {"暖森", "加藤英美里"},class = 60,type = 0},
	[553] = {id = 553,name = "墟上烈阳",fit_gun = 54,voice = 4,substitute_voice = "",dialog = "这把乐器还挺轻的嘛。但是吹奏的经验不是下载资料就立刻能做到的……指挥官，你会吹吗？",note = "",illustrator_cv = {"暖森", "山根希美"},class = 60,type = 0},
	[554] = {id = 554,name = "水中融月",fit_gun = 143,voice = 4,substitute_voice = "",dialog = "我喜欢这种圆融的音色。刚刚的配合不错，请大家再试一次！",note = "",illustrator_cv = {"暖森", "福原綾香"},class = 60,type = 0},
	[555] = {id = 555,name = "旋转彭罗斯",fit_gun = 103,voice = 4,substitute_voice = "",dialog = "我们始终在上行吗？嘻嘻……那往上走和往下走，也没什么区别吧？",note = "",illustrator_cv = {"--", "嶺内ともみ"},class = 64,type = 0},
	[556] = {id = 556,name = "波莱罗舞雀",fit_gun = 101,voice = 4,substitute_voice = "",dialog = "跳起来吧！这么盛大的演出，可不能偷懒哦！",note = "",illustrator_cv = {"--", "能登麻美子"},class = 64,type = 1},
	[557] = {id = 557,name = "敲击波丽露",fit_gun = 65,voice = 4,substitute_voice = "",dialog = "打击乐就打击乐，我要用气势赢过M16A1……G11，你先起来。",note = "",illustrator_cv = {"--", "野中藍"},class = 64,type = 0},
	[558] = {id = 558,name = "混乱交响曲",fit_gun = 122,voice = 4,substitute_voice = "",dialog = "我真的不会指挥啊！好多啊，我的云图要烧掉了！！我要回去睡觉——",note = "",illustrator_cv = {"--", "福原綾香"},class = 64,type = 0},
	[559] = {id = 559,name = "克莱因间奏",fit_gun = 102,voice = 4,substitute_voice = "",dialog = "永远都不可能装满水的瓶子，就像永远也听不到、但至今也持续着的演奏……",note = "",illustrator_cv = {"--", "上間江望"},class = 64,type = 0},
	[560] = {id = 560,name = "庄严行进",fit_gun = 257,voice = 4,substitute_voice = "",dialog = "我还是第一次参加这样隆重的典礼……有点紧张。指挥官呢？",note = "",illustrator_cv = "",class = 60,type = 0},
	[561] = {id = 561,name = "十号小夜曲",fit_gun = 315,voice = 4,substitute_voice = "",dialog = "典礼奏乐之后，也让我为前辈独奏一次吧！曲目我已经选好了。",note = "",illustrator_cv = "",class = 60,type = 0},
	[562] = {id = 562,name = "谐谑天鹅湖",fit_gun = 318,voice = 4,substitute_voice = "",dialog = "按着乐谱来做就可以了吧，感觉也没什么难度。",note = "",illustrator_cv = "",class = 60,type = 0},
	[563] = {id = 563,name = "悠思之晨",fit_gun = 316,voice = 4,substitute_voice = "",dialog = "今天我也在负责的队列……还是有点跟不上节奏，希望能做好吧。",note = "",illustrator_cv = "",class = 60,type = 1},
	[564] = {id = 564,name = "即兴狂想乐",fit_gun = 296,voice = 4,substitute_voice = "",dialog = "次中音萨克斯！我喜欢这个。凭直觉吹也挺好听的嘛！",note = "",illustrator_cv = "",class = 60,type = 0},
	[565] = {id = 565,name = "光辉守望者",fit_gun = 64,voice = 4,substitute_voice = "",dialog = "看啊，那光芒闪耀的地方……那就是我们的目的地吗，长官？",note = "",illustrator_cv = "",class = 60,type = 0},
	[566] = {id = 566,name = "晨光欧蕾",fit_gun = 261,voice = 4,substitute_voice = "",dialog = "欢迎光临罗森！早安，指挥官。今天的早餐有三种，已经帮你准备好最实惠又好吃的了！",note = "",illustrator_cv = "",class = 0,type = 0},
	[567] = {id = 567,name = "墨鸽爵士",fit_gun = 1030,voice = 4,substitute_voice = "",dialog = "不要小看这件斗篷，这可是那位鸽子男爵的东西……一旦穿上了，就会变成超级——大坏蛋！！！",note = "",illustrator_cv = "",class = 53,type = 0},
	[568] = {id = 568,name = "恶魔在身边",fit_gun = 1033,voice = 4,substitute_voice = "",dialog = "虽然衣服本身是很可爱，可我总觉得露肤度太高了……小邪神不、不要碰啊，可恶，德国肘子——",note = "",illustrator_cv = "",class = 53,type = 0},
	[569] = {id = 569,name = "浓情生巧职人",fit_gun = 1032,voice = 4,substitute_voice = "",dialog = "手作的巧克力，送给最重要的人♪就像巧克力有苦有甜一样，我也要陪着重要的人哭着笑着一直走下去！",note = "",illustrator_cv = "",class = 53,type = 0},
	[570] = {id = 570,name = "打工吧！天使大人",fit_gun = 1031,voice = 4,substitute_voice = "",dialog = "波——诺艾尔今天是作为人类的偶像站在这里的，而守护人类就是天使的工作！",note = "",illustrator_cv = "",class = 53,type = 0},
	[571] = {id = 571,name = "湖畔对饮夜",fit_gun = 1034,voice = 4,substitute_voice = "",dialog = "你看，我们现在正被支笏湖怀抱着泡温泉呢！这样的体验可不是每晚都有喔，一起干杯庆祝吧！",note = "",illustrator_cv = "",class = 53,type = 0},
	[572] = {id = 572,name = "秋眠·随想Ⅺ",fit_gun = 122,voice = 4,substitute_voice = "",dialog = "设计师的灵感大都源于一个遐想，优秀的穿衣者善于解读，而这次的模特则更加卓越——她受到强烈的感染，只想睡觉。",note = "",illustrator_cv = {"GINGALA", "福原綾香"},class = 0,type = 0},
	[573] = {id = 573,name = "午后闲弈",fit_gun = 116,voice = 4,substitute_voice = "",dialog = "原来如此，这里要这么走啊……嗯？指挥官也要来下一盘吗？刚刚通读了攻略书的我可是不会输的哦！",note = "",illustrator_cv = "",class = 0,type = 0},
	[574] = {id = 574,name = "回魂修理师Jr",fit_gun = 56,voice = 4,substitute_voice = "",dialog = "喂，你预约的M4 SOPMODII Jr大修理师已经就位了，还在啰嗦什么！修不好怎么办？那当然是通通拆掉啊！",note = "",illustrator_cv = {"Su", "田村ゆかり"},class = 0,type = 0},
	[601] = {id = 601,name = "两人的旅程",fit_gun = 6,voice = 16,substitute_voice = "",dialog = "……两个人之间的事？我不太懂。不过，您会教给我吧？",note = "",illustrator_cv = "",class = 7,type = 0},
	[602] = {id = 602,name = "契约时刻",fit_gun = 21,voice = 16,substitute_voice = "",dialog = "指挥官，和您结下这份契约，是我生命中最满足的时刻，今后请让我将更多的荣耀，带到您的面前吧！",note = "",illustrator_cv = "",class = 7,type = 0},
	[603] = {id = 603,name = "被回应的心意",fit_gun = 29,voice = 16,substitute_voice = "",dialog = "诶？我的心意……被回应了吗？明明没什么讨人喜欢的地方，但是这一天突然就到来了。",note = "",illustrator_cv = "",class = 7,type = 0},
	[604] = {id = 604,name = "毕生守护",fit_gun = 50,voice = 16,substitute_voice = "",dialog = "从此刻开始，请让我永远地守护您，这是我毕生的荣耀！",note = "",illustrator_cv = "",class = 7,type = 1},
	[605] = {id = 605,name = "与您同在",fit_gun = 85,voice = 16,substitute_voice = "",dialog = "请让我继续与您同在，一起扫清前方的道路。",note = "",illustrator_cv = "",class = 7,type = 0},
	[606] = {id = 606,name = "掌心约定",fit_gun = 100,voice = 16,substitute_voice = "",dialog = "哎呀，结果您还是落到我的手心呢。",note = "",illustrator_cv = "",class = 7,type = 0},
	[607] = {id = 607,name = "最重要的日子",fit_gun = 106,voice = 16,substitute_voice = "",dialog = "虽然今天是个重要的日子，不过也别太紧张哦。",note = "",illustrator_cv = "",class = 7,type = 0},
	[701] = {id = 701,name = "返校季",fit_gun = 57,voice = 4,substitute_voice = "",dialog = "这次我的同桌，会是M4A1，还是……",note = "",illustrator_cv = "",class = 8,type = 0},
	[702] = {id = 702,name = "王牌值日生",fit_gun = 97,voice = 4,substitute_voice = "",dialog = "喂……这里的地还没扫干净呢，值日时可不能偷懒啊。",note = "",illustrator_cv = {"音符", "古賀葵"},class = 8,type = 0},
	[703] = {id = 703,name = "生存社员",fit_gun = 125,voice = 4,substitute_voice = "",dialog = "想加入我们社团吗？……生存可不只是游戏哦。",note = "",illustrator_cv = "",class = 8,type = 0},
	[704] = {id = 704,name = "战斗水手",fit_gun = 138,voice = 4,substitute_voice = "",dialog = "浪已经来了，让我们的射程跨越那片大海吧！",note = "",illustrator_cv = "",class = 8,type = 0},
	[801] = {id = 801,name = "深红的星途",fit_gun = 27,voice = 4,substitute_voice = "",dialog = "蝎子的玫瑰有没有毒，和我走到现在的指挥官最清楚了吧？",note = "等到下次派对的时候，我一定要成为主角！",illustrator_cv = "",class = 9,type = 0},
	[802] = {id = 802,name = "灯火下的女王",fit_gun = 36,voice = 4,substitute_voice = "",dialog = "今晚宴会上的聚光灯……一定会见证最美好的时刻。",note = "度过了相当愉快的一年呢，希望来年有更多的孩子能够参加正式宴会啊。",illustrator_cv = "",class = 9,type = 0},
	[803] = {id = 803,name = "月之海",fit_gun = 39,voice = 4,substitute_voice = "",dialog = "没想到我们已经经历这么多了，之后您的身影，也会一直在我们身边吧。",note = "最后我是怎么回宿舍的？有点记不清了……不过管它呢，好久没喝那么畅快了。",illustrator_cv = "",class = 9,type = 2},
	[804] = {id = 804,name = "舞会休止符",fit_gun = 48,voice = 4,substitute_voice = "",dialog = "说好给我当舞伴的，怎么还没来……诶！干、干嘛啦！迟到了就乖乖闭嘴好吗！",note = "用琴盒来携带枪支，本以为是不错的设计却带来了意外的麻烦呢……下回要怎么办才好？",illustrator_cv = "",class = 9,type = 0},
	[805] = {id = 805,name = "星之茧",fit_gun = 65,voice = 4,substitute_voice = "",dialog = "指挥官，让今晚的舞台……变成只属于我们两个人的地方吧。",note = "为什么大家都用畏惧的眼神看着我……昨晚发生什么了吗？",illustrator_cv = "",class = 9,type = 1},
	[806] = {id = 806,name = "绸缪夜曲",fit_gun = 75,voice = 4,substitute_voice = "",dialog = "红地毯准备好了吗？可别让人家摔跤了哦？",note = "呼……虽说昨晚是用来放松的派对，感觉也好累呀。再多睡一会儿好了……",illustrator_cv = "",class = 9,type = 0},
	[807] = {id = 807,name = "黑兔玛奇朵",fit_gun = 135,voice = 4,substitute_voice = "",dialog = "美食搭配可爱的我，作为庆祝今天的贺礼可是特别大放送哦！",note = "总有一天我会成长到能被批准参加正式宴会的！哼哼，到那时一定会让大家羡慕的吧~",illustrator_cv = "",class = 9,type = 0},
	[808] = {id = 808,name = "祭典强音！",fit_gun = 97,voice = 4,substitute_voice = "",dialog = "压轴戏要开始了吗？那就轮到我上喽！",note = "啊哈哈……酒店的仓库好像忘记收拾了。希望没有给工作人员添麻烦……",illustrator_cv = {"音符", "古賀葵"},class = 9,type = 0},
	[901] = {id = 901,name = "洋葱枪手",fit_gun = 50,voice = 4,substitute_voice = "MLEMK1_0",dialog = "诶？我、我怎么会变得……放、放心吧，即便如此我也能战斗哦！……大概……",note = "",illustrator_cv = "",class = 10,type = 0},
	[902] = {id = 902,name = "拇指公主",fit_gun = 25,voice = 4,substitute_voice = "MP40_0",dialog = "指挥官，需要我帮您捶肩吗？……嗯，放心吧！我会很用力的！",note = "",illustrator_cv = "",class = 10,type = 0},
	[903] = {id = 903,name = "变身！泰迪熊！",fit_gun = 96,voice = 4,substitute_voice = "Grizzly_0",dialog = "唔、唔……人生总是这么痛苦吗？还是只有小时候是这样？",note = "",illustrator_cv = "",class = 10,type = 0},
	[904] = {id = 904,name = "小流浪家",fit_gun = 112,voice = 4,substitute_voice = "NEGEV_0",dialog = "指挥官啊，今天不给小孩子礼物可是会被逮捕的哦~",note = "",illustrator_cv = "",class = 10,type = 0},
	[905] = {id = 905,name = "小小招待员",fit_gun = 64,voice = 4,substitute_voice = "G36_0",dialog = "诶？原来我的枪有这么大吗？……那我也要努力长身体才行了。",note = "",illustrator_cv = "",class = 10,type = 0},
	[1101] = {id = 1101,name = "骄阳任务",fit_gun = 53,voice = 4,substitute_voice = "",dialog = "海风里吹来的杀气，就交给我来解决吧。",note = "",illustrator_cv = "",class = 11,type = 0},
	[1102] = {id = 1102,name = "夏鸣蝉",fit_gun = 129,voice = 4,substitute_voice = "",dialog = "真想快点也让97看看这个泳装呢，嘻嘻~",note = "",illustrator_cv = "",class = 11,type = 0},
	[1103] = {id = 1103,name = "仲夏夜的精灵",fit_gun = 115,voice = 4,substitute_voice = "",dialog = "好厉害！……大海和天空居然连在一起了！",note = "",illustrator_cv = "",class = 11,type = 1},
	[1104] = {id = 1104,name = "热带风暴",fit_gun = 146,voice = 4,substitute_voice = "",dialog = "指挥官~让阳光把你和我的影子一起烙印在海面上吧！",note = "",illustrator_cv = "",class = 11,type = 0},
	[1105] = {id = 1105,name = "盛夏巡礼者",fit_gun = 153,voice = 4,substitute_voice = "",dialog = "不光是指挥官，这次我要吸引所有人的目光哦？好好欣赏吧。",note = "",illustrator_cv = "",class = 11,type = 0},
	[1106] = {id = 1106,name = "沙滩公主",fit_gun = 34,voice = 4,substitute_voice = "",dialog = "嘻嘻，夏天的话果然还是穿成这样最舒服了！",note = "",illustrator_cv = "",class = 11,type = 0},
	[1107] = {id = 1107,name = "睡醒的人鱼",fit_gun = 36,voice = 4,substitute_voice = "",dialog = "指挥官，和我一起找一个海风最舒服的位置吧~",note = "",illustrator_cv = "",class = 11,type = 2},
	[1108] = {id = 1108,name = "魔鬼鱼行动",fit_gun = 48,voice = 4,substitute_voice = "",dialog = "泳、泳装既然都买了，不穿就可惜了……可不是为了特地穿给你看哦！",note = "",illustrator_cv = "",class = 11,type = 2},
	[1109] = {id = 1109,name = "游轮女王",fit_gun = 142,voice = 4,substitute_voice = "",dialog = "哼哼~指挥官您真是选了个趣味十足的款式呢，这下满足了吗？",note = "",illustrator_cv = "",class = 11,type = 0},
	[1201] = {id = 1201,name = "花与水",fit_gun = 173,voice = 4,substitute_voice = "",dialog = "是你啊？不要妨碍我，我在找祭典上最好的苹果糖。",note = "",illustrator_cv = "",class = 12,type = 2},
	[1202] = {id = 1202,name = "夏天·烟火·向阳之诗",fit_gun = 104,voice = 4,substitute_voice = "",dialog = "啊、居然在这里遇见您……要和我一起等待烟火的到来吗？",note = "",illustrator_cv = "",class = 12,type = 1},
	[1203] = {id = 1203,name = "樱吹雪",fit_gun = 119,voice = 4,substitute_voice = "",dialog = "难得这么热闹，今晚就别聊工作了，好好享受如何？",note = "",illustrator_cv = "",class = 12,type = 2},
	[1204] = {id = 1204,name = "三羽乌",fit_gun = 177,voice = 4,substitute_voice = "",dialog = "嘿嘿~今晚的祭典游戏我要全部拿第一名！",note = "",illustrator_cv = "",class = 12,type = 0},
	[1205] = {id = 1205,name = "花未眠",fit_gun = 26,voice = 4,substitute_voice = "",dialog = "指挥官，这次的烟火大会是什么样的呢？好期待啊~",note = "",illustrator_cv = "",class = 12,type = 0},
	[1301] = {id = 1301,name = "北极狐",fit_gun = 79,voice = 4,substitute_voice = "",dialog = "稍微认真打扮了一下，嗯……这样也就差不多了吧。",note = "",illustrator_cv = "",class = 13,type = 0},
	[1302] = {id = 1302,name = "子弹咖啡馆",fit_gun = 118,voice = 4,substitute_voice = "",dialog = "指挥官，我的服务是只属于您一个人的……只属于您。",note = "",illustrator_cv = "",class = 13,type = 0},
	[1303] = {id = 1303,name = "战地糕点师",fit_gun = 63,voice = 4,substitute_voice = "",dialog = "需要点心吗？菜单上没有的我也可以试试。",note = "",illustrator_cv = "",class = 13,type = 0},
	[1304] = {id = 1304,name = "假日街景",fit_gun = 88,voice = 4,substitute_voice = "",dialog = "哎呀……这个衣服松松垮垮的，真是麻烦呢。",note = "",illustrator_cv = "",class = 13,type = 0},
	[1305] = {id = 1305,name = "酒吧之夜",fit_gun = 72,voice = 4,substitute_voice = "",dialog = "指挥官，可注意别喝太多了……醉了的话就交给我吧。",note = "",illustrator_cv = "",class = 13,type = 0},
	[1401] = {id = 1401,name = "暗影之王",fit_gun = 114,voice = 18,substitute_voice = "",dialog = "这种装扮是吓不住我的，已经死亡的东西……只是回归了黑暗而已。",note = "",illustrator_cv = "",class = 14,type = 1},
	[1402] = {id = 1402,name = "青丘狐",fit_gun = 127,voice = 18,substitute_voice = "",dialog = "今晚的糖果都准备好了，把大家叫过来吧，不过一次别来太多人哦。",note = "",illustrator_cv = "",class = 14,type = 2},
	[1403] = {id = 1403,name = "第七封印者",fit_gun = 148,voice = 18,substitute_voice = "",dialog = "今晚有什么想玩的吗？我已经准备好了100个鬼故事哦？",note = "",illustrator_cv = "",class = 14,type = 2},
	[1404] = {id = 1404,name = "魔枪修女",fit_gun = 100,voice = 18,substitute_voice = "",dialog = "不给糖果不捣蛋！嘿嘿，我今晚可是收到了很多糖果呢，为什么？当然是因为可爱啦！",note = "",illustrator_cv = "",class = 14,type = 0},
	[1405] = {id = 1405,name = "最后的爱丽丝",fit_gun = 168,voice = 18,substitute_voice = "",dialog = "请放心吧，今晚舞会的死亡摇滚就交给我了。",note = "",illustrator_cv = "",class = 14,type = 2},
	[1406] = {id = 1406,name = "偷走重要的糖果",fit_gun = 70,voice = 18,substitute_voice = "",dialog = "不给糖就捣蛋！不给糖就捣蛋！不给糖就捣蛋！",note = "",illustrator_cv = "",class = 14,type = 0},
	[1407] = {id = 1407,name = "狼行者",fit_gun = 124,voice = 18,substitute_voice = "",dialog = "这是我今晚收到的第一个礼物哦，打开看看吧！……啊！！……果然是陷阱啊……",note = "",illustrator_cv = "",class = 14,type = 0},
	[1501] = {id = 1501,name = "恶魔猎人",fit_gun = 16,voice = 4,substitute_voice = "",dialog = "出入高档场所，必须穿着得体，气质相称，这也是行业的规矩。<br>",note = "没能听完整场歌剧，有点可惜。下次找个无人打扰的机会再去一趟吧。",illustrator_cv = "",class = 15,type = 1},
	[1502] = {id = 1502,name = "歌剧魅影",fit_gun = 183,voice = 4,substitute_voice = "",dialog = "精美的服装也是舞台表演成功的一环，唔……所以服装也是实力的一部分吗？<br>",note = "任务结束后剧院赠送了我一张剧照，如果是正面就更好了……",illustrator_cv = "",class = 15,type = 0},
	[1503] = {id = 1503,name = "淑女密令",fit_gun = 173,voice = 4,substitute_voice = "",dialog = "哼，正确的着装只是让我的胜利更加完美而已。<br>",note = "长官收到发票的时候脸色不太好……这是为什么？",illustrator_cv = "",class = 15,type = 0},
	[1504] = {id = 1504,name = "荒野猎人",fit_gun = 109,voice = 4,substitute_voice = "",dialog = "这样穿的话能增强野外战斗的适应性……嗯，能够更好地保护同伴了。<br>",note = "在战斗的间隙，即使是一杯速溶咖啡也是人间美味。",illustrator_cv = "",class = 15,type = 0},
	[1505] = {id = 1505,name = "极道车神",fit_gun = 149,voice = 4,substitute_voice = "",dialog = "嘿！展现出了不一样的旋律，这样的我依然很酷哦♪<br>",note = "唔……下次骑车的时候，试试新的节奏好了♪",illustrator_cv = "",class = 15,type = 2},
	[1506] = {id = 1506,name = "爆裂骑手",fit_gun = 180,voice = 4,substitute_voice = "",dialog = "那个……这身衣服同样也是很贵的，小心别碰坏了啊！<br>",note = "指挥官，车钥匙借你一副，别忘记我们说好的练习啊！",illustrator_cv = "",class = 15,type = 0},
	[1507] = {id = 1507,name = "“调酒师”",fit_gun = 64,voice = 4,substitute_voice = "",dialog = "Guten Tag，我的长官。G36在此为您效劳。这样行礼……会更像一名管家吗？<br>",note = "这是炸弹的引线……真是危险，不过我一定会保护好大家的。",illustrator_cv = "",class = 15,type = 0},
	[1601] = {id = 1601,name = "RFB偷走圣诞节",fit_gun = 172,voice = 19,substitute_voice = "",dialog = "圣诞节DLC打折啦！指挥官快买来送给我当礼物吧？",note = "",illustrator_cv = "",class = 16,type = 1},
	[1602] = {id = 1602,name = "我最美丽的礼物",fit_gun = 122,voice = 19,substitute_voice = "",dialog = "圣诞快乐……指挥官，晚宴准备好了吗？我待会儿会去吃的。",note = "",illustrator_cv = {"音符", "福原綾香"},class = 16,type = 2},
	[1603] = {id = 1603,name = "献上枪声的雪姑娘",fit_gun = 196,voice = 19,substitute_voice = "",dialog = "要怎样才能让我相信有圣诞老人？除非让我打中他……开玩笑的。",note = "",illustrator_cv = "",class = 16,type = 0},
	[1604] = {id = 1604,name = "凛冬采集者",fit_gun = 166,voice = 19,substitute_voice = "",dialog = "长官你快看，我把圣诞老人的胡子扯下来了！哈哈哈哈哈……真好玩！",note = "",illustrator_cv = "",class = 16,type = 0},
	[1605] = {id = 1605,name = "卖礼物的圣诞树",fit_gun = 185,voice = 19,substitute_voice = "",dialog = "圣诞快乐，指挥官，在袜子里放个纽扣送给圣诞老人吧……",note = "",illustrator_cv = "",class = 16,type = 0},
	[1606] = {id = 1606,name = "如你所愿的圣诞",fit_gun = 75,voice = 19,substitute_voice = "",dialog = "圣诞节？很喜欢哦～还有蛋糕吃呢！",note = "",illustrator_cv = "",class = 16,type = 0},
	[1608] = {id = 1608,name = "森林里的小驯鹿<br>",fit_gun = 145,voice = 19,substitute_voice = "",dialog = "圣诞快乐，指挥官！今晚有很多活动呢，需要我来当向导吗？",note = "",illustrator_cv = "",class = 16,type = 0},
	[1701] = {id = 1701,name = "兔子班",fit_gun = 128,voice = 4,substitute_voice = "",dialog = "嘿嘿~长官，想知道我的电话号码吗？要常联系哦~",note = "",illustrator_cv = "",class = 17,type = 0},
	[1702] = {id = 1702,name = "ThanX！&Go2Hell！",fit_gun = 126,voice = 4,substitute_voice = "",dialog = "这可是现在最流行的款式哦！我从秘密渠道淘来的正品……想称赞的话就不要客气了！",note = "",illustrator_cv = "",class = 17,type = 0},
	[1703] = {id = 1703,name = "拥抱粉雪",fit_gun = 98,voice = 4,substitute_voice = "",dialog = "习惯水下的冰冷后……现在这样温暖的时刻会显得更幸福呢。",note = "",illustrator_cv = "",class = 17,type = 0},
	[1704] = {id = 1704,name = "异乡的十字路口",fit_gun = 156,voice = 4,substitute_voice = "",dialog = "趁现在出发吧，我们前进的方向上没有迷路的时间了。",note = "",illustrator_cv = "",class = 17,type = 0},
	[1705] = {id = 1705,name = "星之彼岸",fit_gun = 159,voice = 4,substitute_voice = "",dialog = "不是工作装也可以吗？……呵呵，好吧，如果这是你希望的服务内容的话。",note = "",illustrator_cv = "",class = 17,type = 0},
	[1801] = {id = 1801,name = "红牡丹",fit_gun = 179,voice = 15,substitute_voice = "",dialog = "指挥官，您的新年计划……会有我的表现机会吗？",note = "虽说没能按照我的愿望封装红包，有些小小的遗憾……不过也看到了有趣的风景呐……",illustrator_cv = "",class = 18,type = 1},
	[1802] = {id = 1802,name = "梅花印",fit_gun = 188,voice = 15,substitute_voice = "",dialog = "转眼间又到新年了呢，指挥官，我们离理想的未来又进了一步吗？",note = "啊……这么可爱的包装，根本舍不得点燃嘛！还是留一个好了……千万不能让ART556发现！",illustrator_cv = "",class = 18,type = 0},
	[1803] = {id = 1803,name = "莲花白",fit_gun = 175,voice = 15,substitute_voice = "",dialog = "指挥官，新年宅在家里太无聊了啦，和我去街上冒险吧！",note = "呜……救命啊！指挥官！我要被毛绒绒怪物吃掉了——！",illustrator_cv = "",class = 18,type = 0},
	[1804] = {id = 1804,name = "玉帘草",fit_gun = 192,voice = 15,substitute_voice = "",dialog = "今年会有什么新年活动吗？要保证我能玩得开心哦。",note = "人类就是容易爱忘事……这个备用纽扣，我帮指挥官存起来好啦！",illustrator_cv = "",class = 18,type = 0},
	[1805] = {id = 1805,name = "蜂糖花",fit_gun = 99,voice = 15,substitute_voice = "",dialog = "新的一年，Darling！注意到我今天的不同了吗？给你十秒钟的时间，要说出七条哦。",note = "这种名叫“饺子”的食物，Darling好像还挺喜欢吃的。那就把配方留下，以后再给Darling做好了~",illustrator_cv = "",class = 18,type = 0},
	[1806] = {id = 1806,name = "雪朝颜",fit_gun = 155,voice = 15,substitute_voice = "",dialog = "新年好，指挥官。就算是假期也请您娱乐适度哦。",note = "剪纸这种工艺还是相当有趣的，也能锻炼自己的耐心。这次参加节日筹办，真是相当有收获呢。",illustrator_cv = "",class = 18,type = 2},
	[1807] = {id = 1807,name = "白山茶",fit_gun = 174,voice = 15,substitute_voice = "",dialog = "指挥官，今年您的目的地在哪里呢？让我继续陪伴您一起前行吧。",note = "这是从旧货店淘到的车票模板……真是令人怀念的物品啊，要好好收藏呢。",illustrator_cv = "",class = 18,type = 0},
	[1808] = {id = 1808,name = "黄星蓟",fit_gun = 81,voice = 15,substitute_voice = "",dialog = "新年快乐，指挥官。去年的财务报表出来了，没有太多浪费，今年也请再接再厉。",note = "这样铺张浪费的节日……虽然和大家一起过得很开心，但平时还是应该注重节俭啊。",illustrator_cv = "",class = 18,type = 0},
	[1809] = {id = 1809,name = "东湖莲",fit_gun = 95,voice = 15,substitute_voice = "",dialog = "主人，过年好！新年有什么打算呢？先来杯咖啡慢慢想吧？",note = "",illustrator_cv = "",class = 18,type = 1},
	[1901] = {id = 1901,name = "盲信者",fit_gun = 20,voice = 16,substitute_voice = "",dialog = "和您闹了这么多别扭，这次，就当作被您捉弄一回吧。",note = "留下这张破坏气氛的照片，真是抱歉啊。但这就是我拍照时的真情实感……大概。",illustrator_cv = "",class = 19,type = 1},
	[1902] = {id = 1902,name = "被接纳的笑容",fit_gun = 142,voice = 16,substitute_voice = "",dialog = "感谢您，请让我今后留在您身边，成为您最信任的同伴吧。<br>",note = "如果有一天能发出婚礼的邀请函……就用这个火漆印好啦。",illustrator_cv = "",class = 19,type = 0},
	[1903] = {id = 1903,name = "小小的一步",fit_gun = 26,voice = 16,substitute_voice = "",dialog = "无论什么地方都很渺小的我，也可以陪在您的身边吗？",note = "果然还是想穿其他人的那种大裙子啊……为了这个目标，今后要努力长高啦！",illustrator_cv = "",class = 19,type = 0},
	[1904] = {id = 1904,name = "纯白矢车菊",fit_gun = 64,voice = 16,substitute_voice = "",dialog = "现在的我已经没事了，只要您在身边，我就能鼓起勇气，面对任何困难！",note = "回来之后发现头发里夹了一点花饰，就留下来当纪念吧。",illustrator_cv = "",class = 19,type = 2},
	[1905] = {id = 1905,name = "同心千千结",fit_gun = 66,voice = 16,substitute_voice = "",dialog = "现在后悔可来不及了，啊哈哈！我可不会放您走啦！",note = "这个印章还挺直白的，要是以后有机会能用到就好了……哈哈，虽然不太可能吧。",illustrator_cv = "",class = 19,type = 0},
	[1906] = {id = 1906,name = "命中红心！",fit_gun = 44,voice = 16,substitute_voice = "",dialog = "一生一次的事，就不需要笔记了吧？",note = "影楼居然还会附赠小礼品吗？不知道真正的婚礼是什么感觉，但收下它稍微有些不好意思……",illustrator_cv = "",class = 19,type = 0},
	[2001] = {id = 2001,name = "远东公主",fit_gun = 194,voice = 4,substitute_voice = "",dialog = "战场上的公主可不是好惹的，不要太早在我面前跪下哦？",note = "",illustrator_cv = "",class = 20,type = 0},
	[2002] = {id = 2002,name = "穹顶月光",fit_gun = 212,voice = 4,substitute_voice = "",dialog = "从过去到现在，那轮月光究竟见证了多少重复的命运呢？",note = "",illustrator_cv = "",class = 20,type = 0},
	[2003] = {id = 2003,name = "凛冬将逝",fit_gun = 177,voice = 4,substitute_voice = "",dialog = "我要让整个寒冬燃烧起来，在这冰冷的世界里留下属于自己的灰烬。",note = "",illustrator_cv = "",class = 20,type = 0},
	[2004] = {id = 2004,name = "玄黄之鸑鷟",fit_gun = 133,voice = 4,substitute_voice = "",dialog = "苍天黄土皆收为我的射程，有胆量的家伙就尽管来吧！",note = "",illustrator_cv = "",class = 20,type = 0},
	[2101] = {id = 2101,name = "最高出价",fit_gun = 179,voice = 4,substitute_voice = "",dialog = "铭记我们今晚的相遇吧，这次的美酒，一定会让您回味无穷的，哼哼……",note = "拥有秘密的女性才是更有女人味的……好奇心和保护自己的想法，哪一个在您的心中更占上风呢？",illustrator_cv = "",class = 21,type = 1},
	[2102] = {id = 2102,name = "星夜舞会",fit_gun = 96,voice = 4,substitute_voice = "",dialog = "指挥官，想在舞台上来跳一段吗？不需要技巧，只要您的步伐热情一点就可以了！",note = "这次的事情真是搞不懂……唉，算了。比起想那些事情，还是问指挥官要补贴更重要啦！",illustrator_cv = "",class = 21,type = 1},
	[2103] = {id = 2103,name = "夜幕危机",fit_gun = 114,voice = 4,substitute_voice = "",dialog = "这场黑暗中的盛典，貌似也有不速之客在蠢蠢欲动，绝对不能放松警惕。",note = "即使对方已经陷入罪恶的沼泽，岸边之人依然会向他抛出一根绳索。这种幼稚而又无法抑制的冲动，人们称之为“友情”。",illustrator_cv = "",class = 21,type = 2},
	[2104] = {id = 2104,name = "白棋王后",fit_gun = 196,voice = 4,substitute_voice = "",dialog = "时间再次回到原点，这期间我们所经历的一切，会使我们向着未来又前进一步……对吧？",note = "真相是相当昂贵的东西啊，指挥官。那么，我的指甲油买回来了吗？",illustrator_cv = "",class = 21,type = 2},
	[2105] = {id = 2105,name = "奇迹女王",fit_gun = 1,voice = 4,substitute_voice = "",dialog = "大家！举起你们手中的可乐！为今晚降临的奇迹干杯吧！",note = "从你将可乐作为赌注的那刻起，你就已经输给我了。",illustrator_cv = "",class = 21,type = 0},
	[2106] = {id = 2106,name = "世界之歌",fit_gun = 101,voice = 4,substitute_voice = "",dialog = "会场好热闹呀！都快找不到45姐了，该不会悄悄一个人跑去吃大餐了吧？",note = "今年的愿望啊……唔，那就希望以后接到的都是像这次这样有趣又轻松的任务吧！",illustrator_cv = {"ZAGALA", "能登麻美子"},class = 21,type = 2},
	[2107] = {id = 2107,name = "钻石之花",fit_gun = 103,voice = 4,substitute_voice = "",dialog = "宴会啊……像这样的气氛，对我来说还真是新鲜呢，指挥官很享受这种氛围吗？",note = "“我把不幸送到了。”……但能够说出这句话，对我来说也许是幸运吧。",illustrator_cv = {"ZAGALA", "嶺内ともみ"},class = 21,type = 2},
	[2108] = {id = 2108,name = "箱中之猫",fit_gun = 93,voice = 4,substitute_voice = "",dialog = "喵哈哈~想要得到会场中的宝藏，可得先过我这一关哦！",note = "虽然这次输了，但我会回来哒喵~！……什么？这是反派的台词吗？我才不信呢喵！",illustrator_cv = "",class = 21,type = 0},
	[2109] = {id = 2109,name = "白夜星语",fit_gun = 131,voice = 4,substitute_voice = "",dialog = "指挥官，酒会的任务完成之后，是否还有小小的庆功宴呢？",note = "我们的表现才是真正重要的……真是的，被这么一说，竟然有一瞬间感觉自己也在闪闪发光了。",illustrator_cv = "",class = 21,type = 0},
	[2201] = {id = 2201,name = "勇敢的小卡诺",fit_gun = 197,voice = 4,substitute_voice = "Carcano1891_0",dialog = "妹妹~我在这边哦！要好好跟着，不然就丢下你了哦！",note = "",illustrator_cv = "",class = 22,type = 2},
	[2202] = {id = 2202,name = "匹诺曹小姐",fit_gun = 198,voice = 4,substitute_voice = "Carcano1938_0",dialog = "等……等一下啊姐姐！你走那么慢，我都快追不上了！",note = "",illustrator_cv = "",class = 22,type = 2},
	[2203] = {id = 2203,name = "芬芬历险记",fit_gun = 142,voice = 4,substitute_voice = "FN57_0",dialog = "指挥官要好好拉着我的手哦？不然您走散了我会很困扰的~",note = "",illustrator_cv = "",class = 22,type = 1},
	[2204] = {id = 2204,name = "青蛙小公主",fit_gun = 136,voice = 4,substitute_voice = "PP19_0",dialog = "这样的身姿作战……也许会让敌人吃惊乱了阵脚呢，值得一试！",note = "",illustrator_cv = "",class = 22,type = 0},
	[2205] = {id = 2205,name = "飞鹅旅行记",fit_gun = 213,voice = 4,substitute_voice = "CBJMS_0",dialog = "毕竟现在是长身体的时候，就算是鱼刺也不能浪费呢……咕，刺有点硬。",note = "",illustrator_cv = "",class = 22,type = 2},
	[2206] = {id = 2206,name = "莉诺尔的乌鸦",fit_gun = 202,voice = 4,substitute_voice = "Thunder50_0",dialog = "没有“爸爸妈妈”的我们……也能算作“孩子”吗？",note = "",illustrator_cv = "",class = 22,type = 0},
	[2301] = {id = 2301,name = "贵族体验馆",fit_gun = 53,voice = 4,substitute_voice = "",dialog = "唔……味太甜了吗？再苦一点会更好吧，主人的口味是倾向哪一种呢？",note = "泡茶真是麻烦的工作……不过还算有趣。休息的时候也偶尔尝试下好了。",illustrator_cv = "",class = 23,type = 1},
	[2302] = {id = 2302,name = "百花侍从",fit_gun = 183,voice = 4,substitute_voice = "",dialog = "那、那个……欢迎回来，指挥……啊不！主、主人！……抱歉，我应该可以做得更好的。",note = "什么？拍照留念吗？这就没必要了吧……呜……穿成这个样子，要怎么面对镜头才好呀……",illustrator_cv = "",class = 23,type = 2},
	[2303] = {id = 2303,name = "家政试炼",fit_gun = 97,voice = 4,substitute_voice = "",dialog = "快让让！把脚抬起来，在这里变成镜子之前可不允许有新的脚印哦。",note = "大扫除之后，无论是人形的精神面貌还是房间都焕然一新！嗯嗯，这次的训练成果相当适合在宿舍中推广！有空就去找指挥官商量一下吧~",illustrator_cv = {"音符", "古賀葵"},class = 23,type = 2},
	[2304] = {id = 2304,name = "啤酒牧场",fit_gun = 146,voice = 4,substitute_voice = "",dialog = "嘿嘿~主人，您要的啤酒来啦！……另一杯？啊、这杯只是为了平衡重量啦！",note = "啊哈哈……G36姐，抱歉！再相信我一次嘛，问你借秘籍的时候我不是答应你了吗？以后我训练再也不偷懒了！",illustrator_cv = "",class = 23,type = 0},
	[2305] = {id = 2305,name = "今日合约",fit_gun = 201,voice = 4,substitute_voice = "",dialog = "这些文件怎么都整理不完呢，我应该早点发现女仆是不会有假期的……",note = "指挥官允诺的假期什么时候才能批下来呢？啊啊……好累啊……",illustrator_cv = "",class = 23,type = 0},
	[2401] = {id = 2401,name = "沙滩朋克2064",fit_gun = 62,voice = 4,substitute_voice = "",dialog = "咦？要玩水吗……可是耳朵会湿掉……衣服也会湿掉的……",note = "沙滩好好玩！大海好好玩！……虽然不知道那块黑色的板子到底是用来做什么的，但是排球也好好玩！",illustrator_cv = "",class = 24,type = 1},
	[2402] = {id = 2402,name = "冰沙时代",fit_gun = 206,voice = 4,substitute_voice = "",dialog = "宁静的事物没有关注的价值，指挥官，你是会为这片大海带来大浪与狂风的人吗？",note = "对冰品的痛觉反馈的确属于性能瑕疵，等到哪一天再也不用战斗时再返厂修复吧。",illustrator_cv = "",class = 24,type = 2},
	[2403] = {id = 2403,name = "太阳永不升起",fit_gun = 163,voice = 4,substitute_voice = "",dialog = "我讨厌大太阳……更讨厌明明讨厌大太阳又跑去海边的自己……",note = "虽然吃太多糖总会出一些什么事……但说了那么多正经话，不吃糖的话也会出事吧。糟糕，快点，需要再补充一些糖分……",illustrator_cv = "",class = 24,type = 2},
	[2404] = {id = 2404,name = "潜水钟与人偶",fit_gun = 205,voice = 4,substitute_voice = "",dialog = "着装完毕，接下来有什么东西要离开水底或是永远埋入深海中，全都交由您决定了。",note = "海底的一切都是静止的，AK12要我“放松”时感觉也是这样……不过并不讨厌就是了。",illustrator_cv = "",class = 24,type = 2},
	[2405] = {id = 2405,name = "糖果枪弹",fit_gun = 28,voice = 4,substitute_voice = "",dialog = "“饲养员”你那是什么表情……游泳这种事情……碰到水再开始学不就够了嘛？",note = "没想到竟然也有人喜欢吃酸糖，或许我们两个能多聊几句……？“饲养员”真该好好向她学习。",illustrator_cv = "",class = 24,type = 2},
	[2406] = {id = 2406,name = "FAL的夏天",fit_gun = 106,voice = 4,substitute_voice = "",dialog = "还在这种时候提起57那就太伤人了，现在，告诉我你对“红色”的看法吧？……嗯？还想发表其他看法吗？",note = "哼哼，这把水枪可是“和平”的象征，还换来了指挥官的“拥抱”！姑且把这标签收藏好吧⋯⋯也算是这个夏日的宝物了。",illustrator_cv = "",class = 24,type = 0},
	[2407] = {id = 2407,name = "和G36的五十天",fit_gun = 64,voice = 4,substitute_voice = "",dialog = "冰沙、水果、还是冷饮呢？不管选择哪一个，在这样的天气还请您务必要留意不要中暑了。",note = "唔……不但晕倒了，还给大家添了麻烦……下一次一定要先做好散热降温的方案。",illustrator_cv = "",class = 24,type = 2},
	[2408] = {id = 2408,name = "盛夏的果实",fit_gun = 162,voice = 4,substitute_voice = "",dialog = "既然是在海边，改造成冲浪板的盾牌应该可以派上用场吧？西瓜……？这可是夏天啊！",note = "把旧物改装成冲浪板虽然很有挑战性，但似乎变成只能在夏天用上了……",illustrator_cv = "",class = 24,type = 0},
	[2409] = {id = 2409,name = "阿梅利的接球",fit_gun = 185,voice = 4,substitute_voice = "",dialog = "真的吗……！要和我一起来打球吗？这次我可是做好了准备哦！",note = "耐心等待的话，总会有好事发生的吧？今、今天的我也成功送出了钮扣喔！",illustrator_cv = "",class = 24,type = 0},
	[2410] = {id = 2410,name = "魔女高徒",fit_gun = 9,voice = 4,substitute_voice = "",dialog = "接下来您看见的都是独家的夏日福利，禁止拍摄也禁止录音哦！",note = "会不会我见到的不是海怪，而是大海里的守护神呢？一定是受到了祂的庇护，所以演唱会才很顺利吧！",illustrator_cv = "",class = 24,type = 0},
	[2501] = {id = 2501,name = "黑疫骑士",fit_gun = 109,voice = 4,substitute_voice = "",dialog = "有的时候您不需要听明白歌词，摇滚是一种“看”的音乐，我的表演会让您明白一切。",note = "",illustrator_cv = "",class = 25,type = 0},
	[2502] = {id = 2502,name = "朵拉贡 Jr.",fit_gun = 122,voice = 4,substitute_voice = "",dialog = "我说……你们明白吵醒熟睡的恐龙会引发什么动静吗？那就听听看吧……",note = "",illustrator_cv = {"音符", "福原綾香"},class = 25,type = 0},
	[2503] = {id = 2503,name = "猎魔法则",fit_gun = 151,voice = 4,substitute_voice = "",dialog = "那些天使的嘶吼和恶魔的祈祷，我会将所有残酷的欲望展现在这个舞台上。",note = "",illustrator_cv = "",class = 25,type = 0},
	[2504] = {id = 2504,name = "钢化蝴蝶",fit_gun = 199,voice = 4,substitute_voice = "",dialog = "不管是什么摇滚，如果跟不上我的鼓点，那就只是单纯的噪音罢了。",note = "",illustrator_cv = "",class = 25,type = 0},
	[2505] = {id = 2505,name = "电子女巫",fit_gun = 152,voice = 4,substitute_voice = "",dialog = "我听见了……大家的欢呼声，就让我用这段Bass Solo 把你们的分贝推向最高潮吧！",note = "",illustrator_cv = "",class = 25,type = 0},
	[2601] = {id = 2601,name = "南瓜串串",fit_gun = 188,voice = 18,substitute_voice = "",dialog = "说到万圣节，果然还得是南瓜呢~不过，这个串串可不是南瓜做的哦？指挥官要尝尝吗？",note = "也，也没有把计划执行得很好啦……我只是尽力做到我能做的。",illustrator_cv = "",class = 26,type = 1},
	[2602] = {id = 2602,name = "精灵狙魔士",fit_gun = 222,voice = 18,substitute_voice = "",dialog = "据说这样的改装需要注入一些魔力才能击中幽灵……不如先注入枫糖浆试试吧？",note = "大家都很努力地想要生存下去，人和人形都是这样的吧？",illustrator_cv = "",class = 26,type = 2},
	[2603] = {id = 2603,name = "幽灵陷阱",fit_gun = 215,voice = 18,substitute_voice = "",dialog = "真正的幽灵可都在诱人的陷阱里等着那些好奇的家伙们……哎呀，匿名版上已经开始直播了哦？",note = "“从今以后，吓人跟开玩笑都会注意分寸的，真对不起。”……呜呜呜……",illustrator_cv = "",class = 26,type = 2},
	[2604] = {id = 2604,name = "枪炮与边箱",fit_gun = 69,voice = 18,substitute_voice = "",dialog = "恶灵们认为自己见识过地狱，那是因为它们没有来过这里……我想听听它们的哀鸣。",note = "总有事情必须要鼓起勇气面对，只要不留在原地就好，对吗，南瓜怪物？",illustrator_cv = "",class = 26,type = 0},
	[2605] = {id = 2605,name = "杰克的三分相",fit_gun = 89,voice = 18,substitute_voice = "",dialog = "糖果、南瓜、鬼怪、这样的日子里，这三个要素都是必不可少的呢。",note = "要达到平衡还真是伤脑筋啊……但知道了能让自己变得更强的新途径，倒也不错。",illustrator_cv = "",class = 26,type = 0},
	[2606] = {id = 2606,name = "黑魔女学院",fit_gun = 178,voice = 18,substitute_voice = "",dialog = "“黑夜的流星~就是相信的魔法~”指挥官！您的扫帚准备好了吗？",note = "今晚本来有个好机会，可以让长官好好欣赏我的诗的。不过，还是等下一次吧~",illustrator_cv = "",class = 26,type = 0},
	[2701] = {id = 2701,name = "击穿锁链的子弹",fit_gun = 208,voice = 4,substitute_voice = "",dialog = "这场演出的子弹，都只为了瞄准那个人的内心，请接收我所有的爱吧~！",note = "",illustrator_cv = "",class = 27,type = 0},
	[2702] = {id = 2702,name = "离别之奏",fit_gun = 192,voice = 4,substitute_voice = "",dialog = "我忘词的时候各位帮我唱一下好不好？之后握手会我会好好补偿大家的啦。",note = "",illustrator_cv = "",class = 27,type = 0},
	[2703] = {id = 2703,name = "翱翔天空之法",fit_gun = 224,voice = 4,substitute_voice = "",dialog = "把你们的荧光棒都举起来！哎呀，可别忘了换成红色的哦？偶像果然非红色不可呢！",note = "",illustrator_cv = "",class = 27,type = 0},
	[2704] = {id = 2704,name = "VRAIN Power",fit_gun = 189,voice = 4,substitute_voice = "",dialog = "只有学院派的偶像才能做到Full Combo！跟上我的节奏就绝对不会掉队哦~",note = "",illustrator_cv = "",class = 27,type = 0},
	[2705] = {id = 2705,name = "奇迹☆开始",fit_gun = 167,voice = 4,substitute_voice = "",dialog = "我就是可爱又迷人的奇迹偶像，HK45哟！大家~跟着我一起Dance吧~",note = "",illustrator_cv = "",class = 27,type = 0},
	[2706] = {id = 2706,name = "群星之绊",fit_gun = 5,voice = 4,substitute_voice = "",dialog = "所谓的偶像啊，就是不论哪个时代，闪耀的光辉永远都那么耀眼哦！",note = "",illustrator_cv = "",class = 27,type = 0},
	[2801] = {id = 2801,name = "圣诞晨间女王",fit_gun = 233,voice = 19,substitute_voice = "",dialog = "圣诞促销才是圣诞节存在的意义，打折的价格才能倍增幸福的价值哦~",note = "这对于现在的我，就是最渴望收获的利益。圣诞快乐， 一起加油吧，格里芬的朋友。",illustrator_cv = "",class = 28,type = 1},
	[2802] = {id = 2802,name = "绯红涡轮",fit_gun = 245,voice = 19,substitute_voice = "",dialog = "本来是想COS圣诞老人的，但送礼的时候大家都在睡觉，没人看见感觉太可怜了啊。",note = "圣诞快乐，指挥官，这句话可不是跟风说的哦。",illustrator_cv = "",class = 28,type = 2},
	[2803] = {id = 2803,name = "扫烟囱的小姑娘",fit_gun = 175,voice = 19,substitute_voice = "",dialog = "如果不想你们的圣诞礼物变成黑炭的话，记得保持烟囱通畅哦~",note = "就算眼前的世界再小，我也能找到快乐。多幸运啊，不是吗？",illustrator_cv = "",class = 28,type = 0},
	[2804] = {id = 2804,name = "恋爱卫星",fit_gun = 164,voice = 19,substitute_voice = "",dialog = "没想到圣诞老人的任务居然如此棘手……那样的胖老头到底是怎么进去的？",note = "好啦，下一次该挑选谁呢？送礼物的路上……再好好考虑一下吧，哼哼……",illustrator_cv = "",class = 28,type = 0},
	[2805] = {id = 2805,name = "蓝色的圣诞",fit_gun = 142,voice = 19,substitute_voice = "",dialog = "指挥官，这么多礼物里偏偏只选了我吗？……呵呵，毕竟是我嘛~",note = "原以为基地里塞满了笨蛋……现在我简直要对她们刮目相看了。",illustrator_cv = "",class = 28,type = 0},
	[2806] = {id = 2806,name = "每个孩子的圣诞梦",fit_gun = 64,voice = 19,substitute_voice = "G36_0",dialog = "我想要这个！还有那个！还有……还有……我想要大家美梦成真！",note = "我……我刚才经历了一个只属于我自己的平安夜。",illustrator_cv = "",class = 28,type = 0},
	[2807] = {id = 2807,name = "红耳朵猫TMP",fit_gun = 176,voice = 19,substitute_voice = "",dialog = "礼物好多啊……穿得轻便一点也更容易送吧，得快点把快乐带给大家才行。",note = "那么……越不像人类的人形才越自由吗？",illustrator_cv = "",class = 28,type = 0},
	[2901] = {id = 2901,name = "新年祈愿",fit_gun = 243,voice = 15,substitute_voice = "",dialog = "指挥官，新年快乐……啊、这个吗？这是荞麦面，我为数不多会做的料理，想试试吗？",note = "",illustrator_cv = "",class = 29,type = 1},
	[2902] = {id = 2902,name = "正月游戏大师",fit_gun = 172,voice = 15,substitute_voice = "",dialog = "Happy new year！指挥官想玩玩今年的年度游戏吗？",note = "",illustrator_cv = "",class = 29,type = 0},
	[2903] = {id = 2903,name = "枪使巫女",fit_gun = 228,voice = 15,substitute_voice = "",dialog = "恭贺新年，指挥官。新的一年，希望能顺顺利利的，一起加油吧。",note = "",illustrator_cv = "",class = 29,type = 0},
	[2904] = {id = 2904,name = "Insegui l'aquilone",fit_gun = 198,voice = 15,substitute_voice = "",dialog = "指挥官您记错了吧？离新年还有一个星期呢。",note = "",illustrator_cv = "",class = 29,type = 2},
	[2905] = {id = 2905,name = "起始的钟声",fit_gun = 197,voice = 15,substitute_voice = "",dialog = "指挥官想要新年的衣服吗？我可以给您设计一款哦~",note = "",illustrator_cv = "",class = 29,type = 2},
	[2906] = {id = 2906,name = "雪月花",fit_gun = 165,voice = 15,substitute_voice = "",dialog = "我调查了各种新年习俗，没想到方式还挺多样的，不如就把每一种都试一遍吧。",note = "",illustrator_cv = "",class = 29,type = 0},
	[2907] = {id = 2907,name = "梦花火",fit_gun = 60,voice = 15,substitute_voice = "",dialog = "新的一年，指挥官。我稍微成长了一些吗？诶，小熊变大了？这是因为刚刚洗过了……",note = "",illustrator_cv = "",class = 29,type = 0},
	[2908] = {id = 2908,name = "新年游园会",fit_gun = 249,voice = 15,substitute_voice = "",dialog = "今天一整天都要把宿舍打扫一遍，指挥官的房间也趁机整理一下吧。",note = "",illustrator_cv = "",class = 29,type = 0},
	[3001] = {id = 3001,name = "紫雨心",fit_gun = 119,voice = 15,substitute_voice = "",dialog = "新年快乐，希望明年也能保持这个状态，让我期待一下吧。",note = "也许牺牲了一点奢侈的时间，但却是还不错的交易呢。",illustrator_cv = "",class = 30,type = 1},
	[3002] = {id = 3002,name = "君影草",fit_gun = 59,voice = 15,substitute_voice = "",dialog = "新的一年也要努力哦，BOSS。别搞错了，只是象征性的吉利话而已，快感谢我吧！",note = "“万事如意”……对吧……除我之外，万事如意……",illustrator_cv = "",class = 30,type = 0},
	[3003] = {id = 3003,name = "朵幽兰",fit_gun = 238,voice = 15,substitute_voice = "",dialog = "新年快乐！指挥官，您有想去郊外走走的计划吗……？我可以跟着一起去吗？",note = "外……外面的世界还是太吓人了……",illustrator_cv = "",class = 30,type = 0},
	[3004] = {id = 3004,name = "薄雪绒",fit_gun = 148,voice = 15,substitute_voice = "",dialog = "新的一年也请多多依靠我吧，不管是胜利还是新年愿望，我都会帮您实现！",note = "即便这里只有我一个人，我也会努力的……更何况……我已经不是一个人了。",illustrator_cv = "",class = 30,type = 0},
	[3005] = {id = 3005,name = "金莲花",fit_gun = 166,voice = 15,substitute_voice = "",dialog = "新年快乐哦，长官。今晚穿这么多衣服啊？还真是弱不禁风呢。",note = "看清了吗，我的脚底下可是平整的，什么都没有。",illustrator_cv = "",class = 30,type = 0},
	[3006] = {id = 3006,name = "山苏子",fit_gun = 26,voice = 15,substitute_voice = "",dialog = "新的一年，希望胸能够成长…加油！",note = "新的一年啊，希望我也能变得更厉害，然后接到更厉害的任务吧！",illustrator_cv = "",class = 30,type = 0},
	[3007] = {id = 3007,name = "琉璃唐",fit_gun = 210,voice = 15,substitute_voice = "",dialog = "时间总会告诉我们新的答案，所以新的一年里也请继续加油吧。",note = "……95，我还能再和你们一起去拜年吗？",illustrator_cv = "",class = 30,type = 0},
	[3101] = {id = 3101,name = "幸福使命",fit_gun = 115,voice = 16,substitute_voice = "",dialog = "终于迎来这一天了吗？感谢您的陪伴，指挥官，我的使命终于完成了……",note = "呜……这张照片是什么时候拍的？！这脆弱的样子太丢人了……指挥官，如果是您偷偷拍的请马上删掉！",illustrator_cv = "",class = 31,type = 1},
	[3102] = {id = 3102,name = "银白之星",fit_gun = 173,voice = 16,substitute_voice = "",dialog = "我是变强了，还是变弱了……长官，就由你来告诉我答案吧。",note = "这次任务也是完美地完成了。我这种精英人形和别人是完全不同的，长官这次应该明白了吧。",illustrator_cv = "",class = 31,type = 0},
	[3103] = {id = 3103,name = "迈出步伐的你",fit_gun = 104,voice = 16,substitute_voice = "",dialog = "看来我也必须变得再可靠一点，才能回应您的期待啊。",note = "之前整理的注意事项似乎用不上呢……就当做留念吧。以后要是还有接这种任务的机会就好了……",illustrator_cv = "",class = 31,type = 2},
	[3105] = {id = 3105,name = "传达到的心意",fit_gun = 99,voice = 16,substitute_voice = "",dialog = "你给予了我新的灵魂，让我重新呼唤你一次吧，Darling……",note = "哼哼，这次只有我和Darling一起拍了照片，真是赚到了！以后我也不会把Darling让给别人的！",illustrator_cv = "",class = 31,type = 2},
	[3106] = {id = 3106,name = "誓约变身",fit_gun = 184,voice = 16,substitute_voice = "",dialog = "我已经……开始喜欢上您那样的眼神了……",note = "虽说这种“变身”也不赖，不过我还是想有一次像赤魂假面那样，真正变身为英雄的经验啊……",illustrator_cv = "",class = 31,type = 0},
	[3107] = {id = 3107,name = "期待着的明天",fit_gun = 201,voice = 16,substitute_voice = "",dialog = "只要有您在身边的话，那份重担……也变得没那么可怕了。",note = "啊……好累啊。不过这次也的确挺开心的……也许是婚纱好看的缘故吧。之后要不要用酬金买件新衣服呢？",illustrator_cv = "",class = 31,type = 0},
	[3201] = {id = 3201,name = "龙狼子",fit_gun = 183,voice = 4,substitute_voice = "",dialog = "“每向前一步，带给我们奇迹的光芒就更加闪耀。您给我的勇气有多坚定，我的刀锋就能前进多远的距离。”",note = "",illustrator_cv = "",class = 32,type = 0},
	[3202] = {id = 3202,name = "黑石姬",fit_gun = 112,voice = 4,substitute_voice = "",dialog = "“地狱的创造者只能带给人们那种程度的痛苦，显然只是它们并未乐在其中。”",note = "",illustrator_cv = "",class = 32,type = 0},
	[3203] = {id = 3203,name = "哥布林猎手",fit_gun = 162,voice = 4,substitute_voice = "",dialog = "“只要没有人……就不会有人受到伤害……为什么以前的我就是想不通呢？”",note = "",illustrator_cv = "",class = 32,type = 0},
	[3204] = {id = 3204,name = "格里芬的舞娘",fit_gun = 6,voice = 4,substitute_voice = "",dialog = "“嘘——告诉我目标在哪里就行了，至于它们是谁、在做什么对我来说都不重要。”",note = "",illustrator_cv = "",class = 32,type = 0},
	[3205] = {id = 3205,name = "斗篷与猫耳",fit_gun = 93,voice = 4,substitute_voice = "",dialog = "“嘿嘿，比起战斗，多放一点又白又圆的小东西到自己身上不是更有帮助吗喵？”",note = "",illustrator_cv = "",class = 32,type = 0},
	[3301] = {id = 3301,name = "黎明之前",fit_gun = 194,voice = 4,substitute_voice = "",dialog = "今天的我一定是比后辈们更加出色的，就请您看着吧，指挥官！",note = "就算是唱歌跳舞，我也要做后辈的榜样！呜哇……指挥官不许拍照！不许！",illustrator_cv = "",class = 33,type = 1},
	[3302] = {id = 3302,name = "静寂苍蓝",fit_gun = 206,voice = 4,substitute_voice = "",dialog = "今天就好好享受一下吧。嗯……要是能遇到有趣的事情，那就更棒了。",note = "呵，也许真会有这么一天吧，指挥官您亲手将邀请函递给我的一天。",illustrator_cv = "",class = 33,type = 2},
	[3303] = {id = 3303,name = "沉默嫣红",fit_gun = 205,voice = 4,substitute_voice = "",dialog = "这是给我的吗？多少有点……啊，没什么。只要您和AK12都觉得这样可以的话，我也没有问题。",note = "下次请不要这样吓我，AK12，我真怕我把你弄丢了……不过今晚很有趣，还是要多谢你。",illustrator_cv = "",class = 33,type = 2},
	[3304] = {id = 3304,name = "白兔夏宴",fit_gun = 128,voice = 4,substitute_voice = "",dialog = "长官，您有工作要交给我吗？啊……抱歉，忘记您说过今天要好好放松了。那……长官，您要来一块蛋糕吗？",note = "不知道指挥官说我需要保护是什么意思，哼，有时间说这个不如建议后厨提高手艺呢！",illustrator_cv = "",class = 33,type = 2},
	[3305] = {id = 3305,name = "酒会观测者",fit_gun = 215,voice = 4,substitute_voice = "",dialog = "为了融入宴会，今天就好好打扮一下吧~哼哼，这样就能得到可以写成首页热贴的秘闻啦！",note = "我可是扑克之神，哈哈！怎么回事，怎么又有拆台的留言！删除！",illustrator_cv = "",class = 33,type = 0},
	[3306] = {id = 3306,name = "星夜狐步",fit_gun = 127,voice = 4,substitute_voice = "",dialog = "这就是充满女子力的服饰吗……啊，并不是不喜欢，只是稍微有点不习惯。长官，今后也请您多指点了！",note = "今晚的我，真的很有魅力吗？不过，只要指挥官能够看得到，那就足够了……",illustrator_cv = "",class = 33,type = 0},
	[3307] = {id = 3307,name = "月光与祖母绿",fit_gun = 146,voice = 4,substitute_voice = "",dialog = "指挥官，今天我的风格也很不错吧？啊哈哈，看来我的努力得到回报了呢。",note = "我可是做好了万全准备呢！……所以，遇到惊喜时才会更开心呀。",illustrator_cv = "",class = 33,type = 0},
	[3308] = {id = 3308,name = "命运华尔兹",fit_gun = 212,voice = 4,substitute_voice = "",dialog = "嘿嘿，看来我的占卜灵验了！指挥官，作为答谢让我来占卜一下您今后的运势吧？",note = "非要说的话……指挥官，我不信那些好运厄运，我只信您。",illustrator_cv = "",class = 33,type = 0},
	[3309] = {id = 3309,name = "黑天鹅的梦",fit_gun = 202,voice = 4,substitute_voice = "",dialog = "这样的场合……我会在一旁默默看着的，请您放心。",note = "庆典确实令人开心，我还替一份甜品取了关于你的名字。下次一起参加舞会吧，好吗，M950A？",illustrator_cv = "",class = 33,type = 0},
	[3401] = {id = 3401,name = "黑猫的赠礼",fit_gun = 65,voice = 4,substitute_voice = "HK416_0",dialog = "指挥官，你想摸摸这只猫吗？给……等、等等！为什么是摸我的头啊？！",note = "",illustrator_cv = "",class = 34,type = 1},
	[3402] = {id = 3402,name = "小小科学家",fit_gun = 236,voice = 4,substitute_voice = "K11_0",dialog = "即使身体变小了，头脑里的知识也一分不少！哼哼，让我们开始实验吧！",note = "",illustrator_cv = "",class = 34,type = 0},
	[3403] = {id = 3403,name = "特工垂耳兔",fit_gun = 103,voice = 4,substitute_voice = "UMP45_0",dialog = "嘻嘻，今天可以出去玩了！指挥官，要好好陪我哦。",note = "",illustrator_cv = {"ZAGALA", "嶺内ともみ"},class = 34,type = 2},
	[3404] = {id = 3404,name = "柴犬侦查员",fit_gun = 101,voice = 4,substitute_voice = "UMP9_0",dialog = "指挥官，今天要带我出去玩吗？您一定有这个安排，对吧对吧？",note = "",illustrator_cv = {"ZAGALA", "能登麻美子"},class = 34,type = 2},
	[3405] = {id = 3405,name = "天使的画笔",fit_gun = 252,voice = 4,substitute_voice = "KSVK_0",dialog = "今天作画的主题是和平。指挥官，您觉得这个主题怎么样？",note = "",illustrator_cv = "",class = 34,type = 0},
	[3406] = {id = 3406,name = "超能少女猪",fit_gun = 145,voice = 4,substitute_voice = "OC44_0",dialog = "您问这个玩偶的事？它是我今天认识的好伙伴。如果您要带我出去玩的话，也一定要带着它哦。",note = "",illustrator_cv = "",class = 34,type = 0},
	[3501] = {id = 3501,name = "长假幸运星",fit_gun = 266,voice = 4,substitute_voice = "",dialog = "不如来打个赌吧，指挥官，如果我赢了，您就陪我去海边；如果您赢了，我就陪你去海边吧。",note = "指挥官为我拍下这张照片的瞬间，一定是我这次海边之旅的运气值到达巅峰的时刻……",illustrator_cv = "",class = 35,type = 1},
	[3502] = {id = 3502,name = "防晒大作战",fit_gun = 253,voice = 4,substitute_voice = "",dialog = "要是在这种地方晒黑，那就会被陌生人当成坏人了！指挥官，能来帮我涂点防晒油吗？",note = "看在你好看的份上，就让你见识一下差点钓上来美人鱼的鱼钩吧！只准看不准拍照的哦……",illustrator_cv = "",class = 35,type = 2},
	[3503] = {id = 3503,name = "进击的飞鱼",fit_gun = 213,voice = 4,substitute_voice = "",dialog = "还发什么呆！赶紧把西瓜、冰品和整片沙滩带过来啊！要是少了这些，我的部下要怎么放松啊？",note = "看什么看啊！不过是几个反叛我的部下做的海报……虽然把我画得还不错就是了……",illustrator_cv = "",class = 35,type = 2},
	[3504] = {id = 3504,name = "反溺水配件",fit_gun = 238,voice = 4,substitute_voice = "",dialog = "原本只是想针对不停沉进水底的问题做出改进，没想到就变成这样了……现在该怎么办……",note = "谢尔久科夫为我量身定做的反溺水挂件……虽然总感觉设计原理不太科学……",illustrator_cv = "",class = 35,type = 2},
	[3505] = {id = 3505,name = "海角四号",fit_gun = 270,voice = 4,substitute_voice = "",dialog = "连载也应该开始考虑进入夏天与水的篇章……既然这样，就必须要更认真取材才行了。",note = "最美好的记忆已经用笔记录在了吾辈的漫画中……当然，也深深烙印在了吾辈的心智云图上。",illustrator_cv = "",class = 35,type = 0},
	[3506] = {id = 3506,name = "黑鸦救生员",fit_gun = 113,voice = 4,substitute_voice = "",dialog = "指挥官，我会在旁边保护您的，请安心享受假期吧。不过还是提醒您，下海游泳前要做好准备运动哦！",note = "这是我参加急救培训获得的奖品，也证明了我的能力，所以溺水什么的……都是谣言！",illustrator_cv = "",class = 35,type = 0},
	[3507] = {id = 3507,name = "游泳课题",fit_gun = 116,voice = 4,substitute_voice = "",dialog = "虽然不是很明白该怎么游泳，既然书上写的教学都那么简单，那就一点问题都没有了！",note = "没有想要写信的对象，我就把几张用不上的邮票放进小说中做书签。但是最近我写了一封信……",illustrator_cv = "",class = 35,type = 0},
	[3601] = {id = 3601,name = "水色青春",fit_gun = 214,voice = 4,substitute_voice = "",dialog = "穿上这件衣服后就没法下水了，稍稍感到有些寂寞……欸这条金鱼是送给我的？它好可爱啊，谢谢您！",note = "",illustrator_cv = "",class = 36,type = 2},
	[3602] = {id = 3602,name = "少女密话",fit_gun = 188,voice = 4,substitute_voice = "",dialog = "今天很热闹呢，指挥官不和她们一起吗？哼哼，我明白了哦，指挥官想和我说话是吧？那就来说悄悄话吧。",note = "",illustrator_cv = "",class = 36,type = 2},
	[3603] = {id = 3603,name = "月下祈愿",fit_gun = 64,voice = 4,substitute_voice = "",dialog = "信笺写好了？那我来帮长官挂到心愿竹上吧，放心我绝不会偷看的。您问我写了什么愿望？呵呵，不能告诉您，因为那样就不灵验了。",note = "",illustrator_cv = "",class = 36,type = 0},
	[3604] = {id = 3604,name = "胧月夜",fit_gun = 79,voice = 4,substitute_voice = "",dialog = "让您久等了，我们走吧。这套衣服我只穿一次哦，因为穿起来太麻烦啦。",note = "",illustrator_cv = "",class = 36,type = 0},
	[3605] = {id = 3605,name = "百花缭乱",fit_gun = 105,voice = 4,substitute_voice = "",dialog = "作为秘密武器，会不会太引人注目了？您说这样才好？嗯……既然您不介意，我就去做出行的准备吧。",note = "",illustrator_cv = "",class = 36,type = 0},
	[3701] = {id = 3701,name = "高校心跳物语",fit_gun = 260,voice = 4,substitute_voice = "",dialog = "嗯？我不可以待在讲台下面吗？不、不要假装蹲下捡掉落的笔，然后凑过来啊！",note = "",illustrator_cv = "",class = 37,type = 1},
	[3702] = {id = 3702,name = "纯白毕业祭",fit_gun = 129,voice = 4,substitute_voice = "",dialog = "棒球社经理毕业典礼结束之后，我在卷筒里发现了一枚纽扣呢……指挥官，那一定是您的吧？是第二颗纽扣对吗？",note = "",illustrator_cv = {"GINGALA", "大門香実"},class = 37,type = 2},
	[3703] = {id = 3703,name = "迟到戏码",fit_gun = 130,voice = 4,substitute_voice = "",dialog = "先叼着面包，接着加速跑，接着在拐角处撞到长官……诶嘿嘿，就算老套，长官您不也还是中计了嘛。",note = "",illustrator_cv = {"Nagu", "佐倉綾音"},class = 37,type = 2},
	[3704] = {id = 3704,name = "特攻队长兔",fit_gun = 135,voice = 4,substitute_voice = "",dialog = "嘿嘿，夜露死苦！要认真给本小姐的特攻服绣字哦！说什么呢，把这衣服交给你，可是等于把命都给你了喂！",note = "",illustrator_cv = "",class = 37,type = 2},
	[3705] = {id = 3705,name = "宣告开学的猫之歌",fit_gun = 99,voice = 4,substitute_voice = "",dialog = "指挥官，看看人家的新校服！新学期又开始了呢，如果能把指挥官也做成小挂件，挂在书包上就好了……",note = "",illustrator_cv = "",class = 37,type = 0},
	[3706] = {id = 3706,name = "放课后的消遣",fit_gun = 155,voice = 4,substitute_voice = "",dialog = "要一起去小卖部吗？周五有限量的炒面面包哦，不知道要提前多久去排队才能买得到呢。",note = "",illustrator_cv = "",class = 37,type = 0},
	[3707] = {id = 3707,name = "课间白日梦",fit_gun = 154,voice = 4,substitute_voice = "",dialog = "嘻嘻，课桌当然是用来午睡的啦，别叫醒我，我刚要拿到全场MVP呢！",note = "",illustrator_cv = "",class = 37,type = 0},
	[3708] = {id = 3708,name = "喵棱镜威力",fit_gun = 176,voice = 4,substitute_voice = "",dialog = "嗷呜……赋予我喵星的力量，变身！怎、怎么了？我在练习学校社团的课题而已……",note = "",illustrator_cv = "",class = 37,type = 0},
	[3801] = {id = 3801,name = "拦路劫糖！",fit_gun = 261,voice = 18,substitute_voice = "",dialog = "哼哼~！感到害怕的话，就快点把免费的糖果交出来！",note = "我、我不会再上当了！这一定是假的！……欸？真的是奖品？那我再报一次名吧！",illustrator_cv = "",class = 38,type = 1},
	[3802] = {id = 3802,name = "灼眼的G36C",fit_gun = 104,voice = 18,substitute_voice = "",dialog = "您在看哪里呢？指挥官……不如猜猜我今晚的角色吧，既不是杀手，也不是被遗弃的电脑哦。",note = "嘘……节目马上要开始了，您的目光，一定要注视着我的改变哦，指挥官。",illustrator_cv = "",class = 38,type = 1},
	[3803] = {id = 3803,name = "南瓜堡垒",fit_gun = 229,voice = 18,substitute_voice = "",dialog = "啊？这是什么？我是谁，我在哪？……万圣节什么的，我还没报名啊！放我出去！",note = "南瓜、南瓜，又是南瓜！等我出来，我就把你们全抓了，混蛋！",illustrator_cv = "",class = 38,type = 0},
	[3805] = {id = 3805,name = "月暗面漫步",fit_gun = 252,voice = 18,substitute_voice = "",dialog = "您还在等什么？准备好南瓜车，狂欢的号角已经吹响，在漩涡中心与我共舞吧，指挥官！",note = "解码，完成……格里芬的诸位，准备好观赏这一出好戏了吗？",illustrator_cv = "",class = 38,type = 0},
	[3806] = {id = 3806,name = "满月请睁眼",fit_gun = 88,voice = 18,substitute_voice = "",dialog = "哼哼，遇到狼人是你的不幸呢……不过，这么晚还在外面你也有错哦，该从哪里开始吃你呢？",note = "在下一个满月到来的时刻……我能找到那个答案吗？",illustrator_cv = "",class = 38,type = 0},
	[3807] = {id = 3807,name = "奇谋妙策九七计",fit_gun = 161,voice = 18,substitute_voice = "",dialog = "如何，这个造型很棒吧？指挥官和大家，就尽情期待今夜我的登场吧！",note = "我把入场券夹在办公桌的玻璃板下面了，希望长官会看到。一定要来参加呀！",illustrator_cv = {"豆芽", "大坪由佳"},class = 38,type = 0},
	[3808] = {id = 3808,name = "魅影突击",fit_gun = 117,voice = 18,substitute_voice = "",dialog = "接招吧，指挥官……到了夜间，就是我们魔物为所欲为的时间了！",note = "看到了吗，m45！我所向披靡的身影！……我……唔，头怎么这么疼……",illustrator_cv = "",class = 38,type = 0},
	[3809] = {id = 3809,name = "暗夜炼狱刹",fit_gun = 223,voice = 18,substitute_voice = "",dialog = "接下来就交给我吧，活在这个世界上的，可不仅仅只有人类。那边的……你们也该反省下了，尤其是你，P7！",note = "因为是傀儡，所以无能为力吗……就让我用一颗子弹，打碎你的伪装！",illustrator_cv = "",class = 38,type = 0},
	[3901] = {id = 3901,name = "茜红领航",fit_gun = 160,voice = 4,substitute_voice = "",dialog = "指挥官，接下来的比赛就拜托啦！我是您的专属领航员，首先请配合我进行身体的检查……",note = "",illustrator_cv = "",class = 39,type = 1},
	[3902] = {id = 3902,name = "跃动冲刺",fit_gun = 242,voice = 4,substitute_voice = "",dialog = "在终点等我吧，指挥官！在这赛场上，只有蔚蓝色的脉动才值得您的注视！",note = "",illustrator_cv = {"陆银", "田所あずさ"},class = 39,type = 2},
	[3903] = {id = 3903,name = "急速风流",fit_gun = 254,voice = 4,substitute_voice = "",dialog = "没错，仪表盘极限的速度才最酷！什么？指挥官，在您的母星上，这是一种风险？",note = "",illustrator_cv = "",class = 39,type = 0},
	[3904] = {id = 3904,name = "热砂声援",fit_gun = 77,voice = 4,substitute_voice = "",dialog = "我会为大家好好加油的！诶，要走这么远吗？那算了……",note = "",illustrator_cv = {"染羽", "岩松泉"},class = 39,type = 0},
	[3905] = {id = 3905,name = "烈旋音波",fit_gun = 10,voice = 4,substitute_voice = "",dialog = "这次的游戏也要遵守规则，赛车可没有安全词可以说哦。明白的话，就说“START！”",note = "",illustrator_cv = {"RIRE", "伊藤あすか"},class = 39,type = 0},
	[4001] = {id = 4001,name = "圣夜约定",fit_gun = 253,voice = 19,substitute_voice = "",dialog = "约定……可不能反悔哦。您问是什么约定？那么快就忘了？给您一个提示吧，是和您相关的哦。",note = "当你给予自然善意，自然也在凝视着你！我听不太懂……但是小松鼠真的好可爱，对吧，指挥官！",illustrator_cv = "",class = 40,type = 1},
	[4002] = {id = 4002,name = "银狐的⅑月台",fit_gun = 208,voice = 19,substitute_voice = "",dialog = "大家今晚渴求礼物的心情是永无止境的，快上来吧，时间可没指挥官想的那么宽裕。",note = "虽然因为“Vixen”的关系，导致我的圣诞节走了不少弯路，但是托指挥官的福，结果还是好的……谢谢。",illustrator_cv = "",class = 40,type = 0},
	[4003] = {id = 4003,name = "120份冬日礼物",fit_gun = 281,voice = 19,substitute_voice = "",dialog = "我把礼物都买回来了，请给我报销119份礼物的钱……还有1份为什么不用？因为……那是我给您买的。",note = "圣诞树上的星星才是最宝贵的礼物，我希望能够把这些光送给所有人，即便它如此微弱。",illustrator_cv = "",class = 40,type = 2},
	[4004] = {id = 4004,name = "埋入心底的宝藏",fit_gun = 228,voice = 19,substitute_voice = "",dialog = "嗯呀……终于挖出来了，咦？这个是……？哇！谢谢，这是最棒的礼物！",note = "抱歉，我的礼物盒里其实空无一物……",illustrator_cv = "",class = 40,type = 0},
	[4005] = {id = 4005,name = "圣诞公主和不笑贝特",fit_gun = 203,voice = 19,substitute_voice = "",dialog = "圣诞快乐，指挥官！想要圣诞礼物的话……嘿嘿，就跟贝特许愿吧！",note = "确实是小小地恶作剧了一下啦……不过这个糖普通地来说也很推荐的哦？指挥官要不要也尝一点呢？",illustrator_cv = {"林檎愛す", "藤井彩加"},class = 40,type = 0},
	[4006] = {id = 4006,name = "无垢庇护者",fit_gun = 201,voice = 19,substitute_voice = "",dialog = "您特地过来是？……啊，是我喜欢的口味，谢谢您……不用担心我，我也在用自己的方式享受节日。",note = "咖啡……好烫。下次要一起喝一杯吗？指挥官。就在下一次的假期里，其他什么也不做。",illustrator_cv = "",class = 40,type = 0},
	[4007] = {id = 4007,name = "12月是我的谎言",fit_gun = 81,voice = 19,substitute_voice = "",dialog = "抱歉哦……我撒了一个谎，但是这个谎言却让我再次见到了您……",note = "努力让我真切感受到自己生存的意义，而休憩让我明白我并非一个人孤独地活着。",illustrator_cv = "",class = 40,type = 0},
	[4101] = {id = 4101,name = "神子上上签",fit_gun = 135,voice = 15,substitute_voice = "",dialog = "锵锵！根据本小姐的预测，指挥官您今天的运势可是最好的！嘻嘻，打扫完神社就一起出门玩吧~",note = "",illustrator_cv = "",class = 41,type = 1},
	[4102] = {id = 4102,name = "福鼠懒春",fit_gun = 122,voice = 15,substitute_voice = "",dialog = "呼……除夕敲钟就不要叫醒我了嘛，好困……呼，还要起个大早去求御守……记得背我去哦……",note = "",illustrator_cv = {"陆银", "福原綾香"},class = 41,type = 2},
	[4103] = {id = 4103,name = "花拍子",fit_gun = 270,voice = 15,substitute_voice = "",dialog = "主公且稍待片刻！新春的比赛就交给吾辈来应对吧！",note = "",illustrator_cv = "",class = 41,type = 0},
	[4104] = {id = 4104,name = "蕊梦矢",fit_gun = 118,voice = 15,substitute_voice = "",dialog = "那个，指挥官……新年的训练，您会来看的吧？请务必待在我身边……",note = "",illustrator_cv = "",class = 41,type = 0},
	[4105] = {id = 4105,name = "贺岁狮舞",fit_gun = 88,voice = 15,substitute_voice = "",dialog = "哈哈，不用怕，指挥官，它只是轻轻‘咬住’您的脖子一下而已，希望您一整年都可以身体健康哦。",note = "",illustrator_cv = "",class = 41,type = 0},
	[4106] = {id = 4106,name = "红梅照雪",fit_gun = 35,voice = 15,substitute_voice = "",dialog = "指挥官，快看，我找到的宝贝！果然只要沉着一点，就可以在雪地里找到宝物呢，嘿……",note = "",illustrator_cv = "",class = 41,type = 0},
	[4107] = {id = 4107,name = "伴君侧",fit_gun = 256,voice = 15,substitute_voice = "",dialog = "小羊羔！到我们许愿了！……欸，你刚刚许了什么新年愿望啊？一定是来年还陪在我的身边，对不对！",note = "",illustrator_cv = "",class = 41,type = 0},
	[4201] = {id = 4201,name = "花月夜行灯",fit_gun = 282,voice = 15,substitute_voice = "",dialog = "传统的新年有很多逸事呢，指挥官。今夜就放下工作，和我一起游廊吧？",note = "在衣柜中讲故事活动的留影纪念，可惜最后一个故事没来得及讲给那个人听呢……",illustrator_cv = "",class = 42,type = 1},
	[4202] = {id = 4202,name = "翠雀媚",fit_gun = 260,voice = 15,substitute_voice = "",dialog = "这样无聊的新年里，指挥官不想跟我玩点刺激的活动吗？比如，三分钟内摸到这把手枪我就答应您一个请求？",note = "这张结构图清晰易懂，不仅便于将椅子按自己的喜好进行改装，还很便于将被炸毁的木板重新变回——不，没什么……",illustrator_cv = "",class = 42,type = 1},
	[4203] = {id = 4203,name = "青竹矜",fit_gun = 173,voice = 15,substitute_voice = "",dialog = "我没必要参与那些乱糟糟的新年比赛，长官。我只要坐在这里，赢家就已经很明显了，不是吗？",note = "包装家具用剩的材料，直接丢了有些浪费，用来挂在指挥官送我的灯笼上倒是效果挺好。",illustrator_cv = "",class = 42,type = 0},
	[4204] = {id = 4204,name = "棣棠卿",fit_gun = 234,voice = 15,substitute_voice = "",dialog = "新年就要用物理消灾法来祈福，这是我擅长的工作。让我以东西方结合的方式，来为今年的黎明献上礼炮！",note = "可以算是我有史以来最手忙脚乱的一次任务了，连费用的报销单都积了厚厚的一沓，辛苦是真的，开心也是真的……",illustrator_cv = "",class = 42,type = 0},
	[4205] = {id = 4205,name = "悬铃绯",fit_gun = 263,voice = 15,substitute_voice = "",dialog = "长官，我正在为我未来准备拍摄的电影寻找演员，您看这只新年吉祥物适合太空飞鼠的角色吗？",note = "以哈尔九千二世为主题的护符周边，等我的电影上映了，一定会引发新的流行风潮。",illustrator_cv = "",class = 42,type = 0},
	[4206] = {id = 4206,name = "珠梅雪",fit_gun = 181,voice = 15,substitute_voice = "",dialog = "嘻嘻，新年快乐哦，指挥官！新春特供秘制珍珠奶茶、超大杯一份！请您签收~",note = "执行任务时的意外之喜！能排进我榜单前十的奶茶店！如果可以的话，我们一起喝一杯吧……",illustrator_cv = "",class = 42,type = 0},
	[4207] = {id = 4207,name = "蓝莲夜",fit_gun = 98,voice = 15,substitute_voice = "",dialog = "快来看我为了庆贺新年做的改进啊！呜哇——怎么失灵了？！指挥官，都说了不要看了啊！",note = "看见这张卡套就想起了我的水下表演首秀，虽然最后还是以闹剧收场，但是多少帮上忙了，对吧？",illustrator_cv = "",class = 42,type = 0},
	[4208] = {id = 4208,name = "杓兰雅",fit_gun = 275,voice = 15,substitute_voice = "",dialog = "鞭炮已经放完了吗？还好放在车里的炸鸡没被发现……指挥官，要来和我一起享用吗？",note = "",illustrator_cv = "",class = 42,type = 0},
	[4209] = {id = 4209,name = "丁香意",fit_gun = 262,voice = 15,substitute_voice = "",dialog = "啊，指挥官，从您的印象来看，我这次新春佳节的装扮，应该没有选错吧？希望您能喜欢呢……",note = "继老式拨盘电话机后的新发现——公用电话IC卡！所以我最近的目标就是寻找到还能使用的公用电话机，然后用它给某人打个电话……",illustrator_cv = "",class = 42,type = 0},
	[4210] = {id = 4210,name = "碧桃仙",fit_gun = 87,voice = 15,substitute_voice = "",dialog = "嘘——这身装扮只给您看过哦！要是妹妹知道了，一定会闹着让我再做一套的……",note = "匆忙接下任务，甚至没有注意到自己领成了安保组的工牌……而等任务结束后，也不必再换成迎宾组的工牌了，或许，正如那声不必说出口的再见。",illustrator_cv = "",class = 42,type = 0},
	[4301] = {id = 4301,name = "手心的朱砂痣",fit_gun = 46,voice = 16,substitute_voice = "",dialog = "挑了很久的捧花，最后还是觉得普通的红玫瑰最适合表达我说不出口的话。",note = "维持信任需要更多的付出，双方都是。不要让我失望。",illustrator_cv = {"Yukuso", ""},class = 43,type = 1},
	[4302] = {id = 4302,name = "柳莺与蔷薇",fit_gun = 97,voice = 16,substitute_voice = "",dialog = "总感觉少了点什么，要是有那个的话就好了……诶？你怎么会知道是香槟色的玫瑰花环？",note = "沙弗莱石和我很相配？好吧，那就选这个了。",illustrator_cv = {"八号地球", "古賀葵"},class = 43,type = 1},
	[4303] = {id = 4303,name = "星夜落雨之时",fit_gun = 96,voice = 16,substitute_voice = "",dialog = "就是这里，现在请你抬头看向天空。3、2、1！怎么样？今年和我看的第一场流星雨，还不错吧？",note = "很不错的场景吧？我借RMB93的相机拍的。",illustrator_cv = "",class = 43,type = 0},
	[4304] = {id = 4304,name = "花之后的情愫",fit_gun = 196,voice = 16,substitute_voice = "",dialog = "本想采些鹅掌草带给指挥官，可是转念一想到它的花语，又总觉得有些不够现实呢……",note = "花、星星和新衣服，还有指挥官的话语，都会保存在我的云图中。",illustrator_cv = "",class = 43,type = 1},
	[4305] = {id = 4305,name = "天使像前的心跳",fit_gun = 165,voice = 16,substitute_voice = "",dialog = "您这次迟到了10分41秒23毫秒，和之前迟到的时间加在一起就是……52分整……",note = "要把对天使大人的心声加密保存才行呢。好，就用这个做封条和提示吧！",illustrator_cv = "",class = 43,type = 0},
	[4306] = {id = 4306,name = "山茶小姐的特别服务",fit_gun = 7,voice = 16,substitute_voice = "",dialog = "呜哇——有没有感觉到惊喜和幸福呢？有的话请支付一下服务费、道具费、场地准备费……",note = "这可不是欠条，只是收据而已！收据！",illustrator_cv = {"_椿日_", "喜多村英梨"},class = 43,type = 0},
	[4308] = {id = 4308,name = "草莓蛋糕与波斯花",fit_gun = 70,voice = 16,substitute_voice = "",dialog = "送你波斯花，等你结束工作，和你一起吃蛋糕，让你喂我最后一颗草莓，要是能一直这样就好啦！",note = "对不起！但巧克力真的很好吃……下次我会用工资补上零食钱的……",illustrator_cv = "",class = 43,type = 0},
	[4309] = {id = 4309,name = "触不到的月光情人",fit_gun = 159,voice = 16,substitute_voice = "",dialog = "如果您选择了红玫瑰，我是不是就可以成为您心中永远的白月光？",note = "真是美好的月夜，对吧，指挥官？您的表情我也好好保存下来了哟。",illustrator_cv = "",class = 43,type = 0},
	[4310] = {id = 4310,name = "某不科学的向日葵",fit_gun = 8,voice = 16,substitute_voice = "",dialog = "虽然知道向日葵追随着太阳转动是因为生长素的作用，但我愿意永远跟在您身边的原因无法用科学解释……",note = "头好痛，总觉得好像忘记了什么事……真奇怪，我昨晚到底……",illustrator_cv = {"豆芽", "上坂すみれ"},class = 43,type = 0},
	[4401] = {id = 4401,name = "无烟棒棒糖",fit_gun = 163,voice = 4,substitute_voice = "",dialog = "放心吧，我没有染上吸烟的恶习，这是棒棒糖！只是我汲取糖分的速度过快导致冒烟而已……",note = "",illustrator_cv = "",class = 44,type = 0},
	[4402] = {id = 4402,name = "奇妙山药饼",fit_gun = 260,voice = 4,substitute_voice = "",dialog = "和我一起吃好吃的山药饼，玩刺激的游戏吧！至于输给我的惩罚嘛……这种悄悄话当然要到被窝里说……",note = "",illustrator_cv = "",class = 44,type = 0},
	[4403] = {id = 4403,name = "可燃乌龙茶",fit_gun = 227,voice = 4,substitute_voice = "",dialog = "在伏特加里兑上一点威士忌，派对的饮料乌龙茶就做好了！来吧，女士们，狂欢的时刻开始了！",note = "",illustrator_cv = "",class = 44,type = 0},
	[4404] = {id = 4404,name = "恶魔指环糖",fit_gun = 269,voice = 4,substitute_voice = "",dialog = "如果你们再朝我扔枕头，我一定会奋战到底，以牙还牙，三倍奉还！",note = "",illustrator_cv = "",class = 44,type = 0},
	[4405] = {id = 4405,name = "碳酸跳跳糖",fit_gun = 137,voice = 4,substitute_voice = "",dialog = "恐怖小说中只在夜晚跳动的黑影，倒进嘴里就会在舌尖上兴奋蹦跳的碳酸，这就是我今天的装扮的灵感！",note = "",illustrator_cv = "",class = 44,type = 0},
	[4406] = {id = 4406,name = "百味咖喱兔",fit_gun = 190,voice = 4,substitute_voice = "",dialog = "听说派对不允许带味道重的食物，我只好带上我的好伙伴咖喱兔了，什么咖喱味？没有的……",note = "",illustrator_cv = "",class = 44,type = 0},
	[4501] = {id = 4501,name = "晚宴的统治者",fit_gun = 119,voice = 4,substitute_voice = "",dialog = "这一切仅仅是对现实的观察。而礼仪与社会规则如此薄弱，我必须成为你的守护者。",note = "统治者？不，我只是享受这种气氛。",illustrator_cv = "",class = 45,type = 1},
	[4502] = {id = 4502,name = "战地的采访者",fit_gun = 257,voice = 4,substitute_voice = "",dialog = "我听说有些杀手会把相机和枪结合在一起，但我相信摄影绝不是谋杀，而是留下时光残片的方式。",note = "我想让更多的花在大地上盛开。",illustrator_cv = "",class = 45,type = 0},
	[4503] = {id = 4503,name = "自然的探索者",fit_gun = 261,voice = 4,substitute_voice = "",dialog = "报酬是免费的艺术照？那当然是让我来！今天的风很适合放风筝呢，这样送出去就能飞得很高。",note = "真是的，就算只有我能做，也不能老是给我这样的急单啊！",illustrator_cv = "",class = 45,type = 0},
	[4504] = {id = 4504,name = "规则的实行者",fit_gun = 143,voice = 4,substitute_voice = "",dialog = "女士进入会场的时候，应该像一只蝴蝶。轻巧，又优雅……不要吵，SOPII。这是很严肃的场合……至少不要跑啊。",note = "这个假期已经很放松了。没有吗？我真的这么认为。",illustrator_cv = {"--", "福原綾香"},class = 45,type = 2},
	[4505] = {id = 4505,name = "时光的定格者",fit_gun = 55,voice = 4,substitute_voice = "",dialog = "记忆淡去时，就像一朵纸上的花。不能让这些鲜活的回忆变得那么苍白……我要牢牢地钉住时间。",note = "为了这一刻的快乐能够持续，无论前方还有多少困难……",illustrator_cv = {"Nagu", "戸松遥"},class = 45,type = 2},
	[4506] = {id = 4506,name = "道路的指引者",fit_gun = 54,voice = 4,substitute_voice = "",dialog = "怎么了？别犹豫，从这边走。这里是安全的，我保证。",note = "虽然我还被限制着喝酒，不过收集酒标作为纪念还是没问题的吧，M4？",illustrator_cv = {"陆银", "山根希美"},class = 45,type = 2},
	[4507] = {id = 4507,name = "酒席的扫荡者",fit_gun = 56,voice = 4,substitute_voice = "",dialog = "哇，这里，这里还有这里都好漂亮啊！我喜欢这里！别拦着我呀RO，我没喝醉！那点气泡酒……",note = "一定要把这个贴在基地里吗，指挥官？不要嘛！",illustrator_cv = {"八号地球", "田村ゆかり"},class = 45,type = 2},
	[4508] = {id = 4508,name = "梦境的囚困者",fit_gun = 57,voice = 4,substitute_voice = "",dialog = "不论是悲伤还是痛苦，亦或者这场无休无止的雨，都总有一天会停下的。",note = "真是混乱的宴会……算了，也不差。",illustrator_cv = "",class = 45,type = 2},
	[4509] = {id = 4509,name = "楚门的旅行者",fit_gun = 252,voice = 4,substitute_voice = "",dialog = "人们的经历就像没有剧本也没有提示卡的电影。当我们迷茫时，就出去走走吧。",note = "人生的乐趣中应该有这么一项：应付意外，并从中找到快乐。",illustrator_cv = "",class = 45,type = 0},
	[4510] = {id = 4510,name = "晨昏的裁定者",fit_gun = 160,voice = 4,substitute_voice = "",dialog = "舞会倒是没什么不好啦，只要结束后不要满地狼藉就好……不觉得乱丢垃圾会侮辱明天的晨光吗？",note = "受骗归受骗，但欣赏到美少女的另一面，是我赚了！",illustrator_cv = "",class = 45,type = 1},
	[4511] = {id = 4511,name = "黑夜的舞蹈者",fit_gun = 284,voice = 4,substitute_voice = "",dialog = "黑夜与爱和死亡相关，爱上你是我生命的开始，也是梦的结束。怎么样，这句话是不是比SVD有深度得多？",note = "切蛋糕怎么能用那么粗鲁的方式！指挥官，你来评判一下！",illustrator_cv = "",class = 45,type = 0},
	[4514] = {id = 4514,name = "天空的击破者",fit_gun = 2,voice = 4,substitute_voice = "",dialog = "人形不会哭泣，那是上天赐予人类的礼物。若人形有爱的话，就让我击破天空，下一场雨送给你吧。",note = "切蛋糕当然是八分直接切比较好了，指挥官您也这么想吧？",illustrator_cv = "",class = 45,type = 0},
	[4601] = {id = 4601,name = "星夜捕猫人",fit_gun = 306,voice = 4,substitute_voice = "AKAlfa_0",dialog = "我乘着一艘荡漾在银河里的小船，与星夜长眠……请相信想象力，没有什么是不可能的。",note = "",illustrator_cv = "",class = 46,type = 1},
	[4602] = {id = 4602,name = "小小乘风人",fit_gun = 303,voice = 4,substitute_voice = "BrowningHP_0",dialog = "只要我的速度够快，总有一天，我一定可以乘着风骑到云彩的上面……到时候，就可以知道它到底是棉花糖还是冰淇淋了！",note = "",illustrator_cv = "",class = 46,type = 2},
	[4603] = {id = 4603,name = "白蔷薇少女",fit_gun = 251,voice = 4,substitute_voice = "X95_0",dialog = "指挥官……可以帮我梳一下头发吗？",note = "",illustrator_cv = "",class = 46,type = 0},
	[4604] = {id = 4604,name = "鸟与森语者",fit_gun = 69,voice = 4,substitute_voice = "FAMAS_0",dialog = "日出日落，飞鸟也不是昨日之鸟，日落日出，清风却也是明日之风。",note = "",illustrator_cv = "",class = 46,type = 0},
	[4605] = {id = 4605,name = "赤红破坏者",fit_gun = 184,voice = 4,substitute_voice = "T5000_0",dialog = "是赤红不是粉红！坚持自己的原则才算是英雄！指挥官，跟我一起维护正义吧！",note = "",illustrator_cv = "",class = 46,type = 0},
	[4606] = {id = 4606,name = "黑猫幼稚园",fit_gun = 176,voice = 4,substitute_voice = "TMP_0",dialog = "喜欢的心意能够胜过一切……就，就算是胆小鬼也可以变勇敢吗？",note = "",illustrator_cv = "",class = 46,type = 0},
	[4607] = {id = 4607,name = "EVO 3的早晨",fit_gun = 131,voice = 4,substitute_voice = "EVO3_0",dialog = "每天早上都要遇到点怪事……VZ61！是你干的吧！",note = "",illustrator_cv = "",class = 46,type = 0},
	[4702] = {id = 4702,name = "逆戟之行",fit_gun = 234,voice = 4,substitute_voice = "",dialog = "指挥官，给您一个小建议，在海边休假的时候就不要穿着制服了吧？这样可不方便骑乘逆戟鲸探险。",note = "",illustrator_cv = "",class = 47,type = 2},
	[4703] = {id = 4703,name = "旖梦之隅",fit_gun = 307,voice = 4,substitute_voice = "",dialog = "背上的防晒霜擦了一半就够不到了……这种半途而废的感觉可真讨厌。",note = "",illustrator_cv = "",class = 47,type = 1},
	[4704] = {id = 4704,name = "怒涛之巅",fit_gun = 296,voice = 4,substitute_voice = "",dialog = "怎么，这就想和我比试一下了？虽然很想鼓励你的勇气，不过还是提前想一下输掉之后的惩罚比较好哦。",note = "",illustrator_cv = "",class = 47,type = 2},
	[4705] = {id = 4705,name = "冰冻之夏",fit_gun = 136,voice = 4,substitute_voice = "",dialog = "格琳娜也太节约了吧！为了不开空调，居然就放这么几块冰来给我们散热……指挥官，想点办法啊！",note = "",illustrator_cv = "",class = 47,type = 0},
	[4706] = {id = 4706,name = "甜慕之心",fit_gun = 286,voice = 4,substitute_voice = "",dialog = "Honey♪和我一起徜徉在葡萄汽水的甜蜜海洋里吧！胆敢反对的人形，我就从你们的素体身上碾过去！",note = "",illustrator_cv = "",class = 47,type = 0},
	[4707] = {id = 4707,name = "南国之风",fit_gun = 187,voice = 4,substitute_voice = "",dialog = "指挥官，今天的行程还没有敲定吧？海边好像也会有折扣活动，要一起去看看吗？",note = "",illustrator_cv = "",class = 47,type = 0},
	[4708] = {id = 4708,name = "阿斯特拉的泳池漂流",fit_gun = 14,voice = 4,substitute_voice = "",dialog = "既然都来玩了就不要再想工作了，快来一起游泳啦，指挥官！等游累了，就一起去吃很多很多好吃的吧！",note = "",illustrator_cv = "",class = 47,type = 0},
	[4709] = {id = 4709,name = "伞中之梦",fit_gun = 51,voice = 4,substitute_voice = "",dialog = "那、那个，指挥官，外面太阳还很大，我准备好了阳伞，要不要……来休息一下？",note = "",illustrator_cv = "",class = 47,type = 0},
	[4801] = {id = 4801,name = "黑铁之心",fit_gun = 53,voice = 4,substitute_voice = "",dialog = "对方有多少人都没用。它们没有突破的可能，因为我在这里。",note = "",illustrator_cv = "",class = 48,type = 2},
	[4802] = {id = 4802,name = "葬仪阵列",fit_gun = 73,voice = 4,substitute_voice = "",dialog = "我准备好了。走吧，到前线去……为敌人献上哀乐与奠花。",note = "",illustrator_cv = {"RIRE", "和氣あず未"},class = 48,type = 1},
	[4803] = {id = 4803,name = "白钢锐刃",fit_gun = 39,voice = 4,substitute_voice = "",dialog = "就算没有马，我也比步兵快！雇佣我吧，城主同志！",note = "",illustrator_cv = "",class = 48,type = 0},
	[4804] = {id = 4804,name = "硝烟号令",fit_gun = 121,voice = 4,substitute_voice = "",dialog = "当这面旗帜挥起的时候，绞肉机就会启动了。哼哼……要跟紧我的脚步哦？",note = "",illustrator_cv = {"山河草莓", "立花理香"},class = 48,type = 2},
	[4805] = {id = 4805,name = "碧空巡逻人",fit_gun = 152,voice = 4,substitute_voice = "",dialog = "所有异动都逃不过我的眼睛，请放心把巡逻任务交给我吧，指挥官大人！诶，现在要叫城主大人吗？",note = "",illustrator_cv = "",class = 48,type = 0},
	[4901] = {id = 4901,name = "三途川女将",fit_gun = 282,voice = 4,substitute_voice = "",dialog = "消疲解乏的五右卫门风吕已经备好，请入瓮吧。",note = "",illustrator_cv = "",class = 49,type = 2},
	[4902] = {id = 4902,name = "暗闇荼蘼",fit_gun = 109,voice = 4,substitute_voice = "",dialog = "那些隔过黑暗的花与水，嗯，每次读到这里都能感受到那种绝望呢。",note = "",illustrator_cv = {"Mxixi", "小清水亜美"},class = 49,type = 2},
	[4903] = {id = 4903,name = "竹影清兵卫",fit_gun = 263,voice = 4,substitute_voice = "",dialog = "竹是我的道场，请由在下收割残心！",note = "",illustrator_cv = "",class = 49,type = 2},
	[4904] = {id = 4904,name = "藤萝歌仙",fit_gun = 266,voice = 4,substitute_voice = "",dialog = "梦里相逢人不见，若知是梦何须醒。与您的邂逅恍如一场甜梦，我有预感不久之后会再相遇。",note = "",illustrator_cv = "",class = 49,type = 1},
	[4905] = {id = 4905,name = "玄衣神乐",fit_gun = 228,voice = 4,substitute_voice = "",dialog = "这是我的首次神签助勤，嘘——我可以先给你抽一支试试运气哦！",note = "",illustrator_cv = "",class = 49,type = 2},
	[4906] = {id = 4906,name = "食魂料理人",fit_gun = 305,voice = 4,substitute_voice = "",dialog = "您问这盒中所装之物？是我制作的必杀料理！第一口请您从中间下筷，破坏了平衡可不好哦。",note = "",illustrator_cv = "",class = 49,type = 2},
	[4907] = {id = 4907,name = "花鸟风月",fit_gun = 182,voice = 4,substitute_voice = "",dialog = "唔……薰风沉醉，难得的安宁平静呢……啊——一不小心喝光了！",note = "",illustrator_cv = "",class = 49,type = 0},
	[4908] = {id = 4908,name = "不倒猫侍",fit_gun = 93,voice = 4,substitute_voice = "",dialog = "喵嗷！小心哦，不要像江户那个天下第一的剑豪，被猫儿夺去了灵魂！",note = "",illustrator_cv = "",class = 49,type = 0},
	[5101] = {id = 5101,name = "垂枝梦狐影",fit_gun = 316,voice = 18,substitute_voice = "",dialog = "金轮挂枝头，狐仙入梦去……做个好梦啊，指挥官。",note = "",illustrator_cv = "",class = 50,type = 1},
	[5102] = {id = 5102,name = "死寂舞者",fit_gun = 276,voice = 18,substitute_voice = "",dialog = "不要再往前走了，指挥官。地狱门前孤立无援，因为上帝不在那儿。",note = "",illustrator_cv = "",class = 50,type = 2},
	[5103] = {id = 5103,name = "魔女格莱特",fit_gun = 130,voice = 18,substitute_voice = "",dialog = "走快一点啦，长官！糖果屋就在前面，晚了就要被95姐没收啦！",note = "",illustrator_cv = {"Nagu", "佐倉綾音"},class = 50,type = 0},
	[5104] = {id = 5104,name = "灯影魔翼",fit_gun = 295,voice = 18,substitute_voice = "",dialog = "灯影苕片和绣球鱼翅都能做微辣，指挥官，胆子莫那么小嘛。这种程度哪个都能吃……等等，指挥官！不要死啊！",note = "",illustrator_cv = "",class = 50,type = 0},
	[5105] = {id = 5105,name = "非凡者学徒",fit_gun = 297,voice = 18,substitute_voice = "",dialog = "我一定会成为出色的非凡者，在这之前得先想办法让女巫小姐留下我才行……",note = "",illustrator_cv = "",class = 50,type = 0},
	[5106] = {id = 5106,name = "甜夜盗贼",fit_gun = 6,voice = 18,substitute_voice = "",dialog = "不给糖就捣乱哦！诶，真的没有吗？那就不要勉强了，我去找别人就好。",note = "",illustrator_cv = "",class = 50,type = 0},
	[5107] = {id = 5107,name = "魇域守门人",fit_gun = 38,voice = 18,substitute_voice = "",dialog = "今天要讲一个鬼故事。从前，有一个守门的女巫……诶，别走啊！给你糖！",note = "",illustrator_cv = "",class = 50,type = 0},
	[5201] = {id = 5201,name = "红色贝雷帽",fit_gun = 104,voice = 4,substitute_voice = "",dialog = "目标进入视线，注意隐蔽！呼……这么认真的场合，我也得好好努力一下了。",note = "",illustrator_cv = "",class = 51,type = 1},
	[5202] = {id = 5202,name = "赤月追猎者",fit_gun = 296,voice = 4,substitute_voice = "",dialog = "别想跑。即使用时间和命运当幌子，我也会——找到目标！特种作战部队所属，SL8，准备突击！",note = "",illustrator_cv = "",class = 51,type = 0},
	[5203] = {id = 5203,name = "第八页守密人",fit_gun = 318,voice = 4,substitute_voice = "",dialog = "口令：‘秘密应该永远被埋葬’。是的，我是VHS。目标确认更改，我将立刻前往任务地点。",note = "",illustrator_cv = "",class = 51,type = 2},
	[5204] = {id = 5204,name = "绝地战警SS",fit_gun = 156,voice = 4,substitute_voice = "",dialog = "狙击手，立刻寻找掩体！报告目标参数，确认锁定……我将为各位提供援护！",note = "",illustrator_cv = "",class = 51,type = 0},
	[5205] = {id = 5205,name = "神秘行者SA",fit_gun = 304,voice = 4,substitute_voice = "",dialog = "摘下头盔就意味着战败。停下您的脚步，不要……算了，我输给您了！请抱抱我！",note = "",illustrator_cv = "",class = 51,type = 2},
	[5206] = {id = 5206,name = "窃听疑云",fit_gun = 244,voice = 4,substitute_voice = "",dialog = "简易电台架设完成，确认周围无人类与异常讯号……通讯接入。请回答：‘秘密是被否定的事实，因此？’",note = "",illustrator_cv = "",class = 51,type = 0},
	[5301] = {id = 5301,name = "圣夜警探",fit_gun = 312,voice = 19,substitute_voice = "",dialog = "我是来送礼物的，请配合行动，不要紧张……这么说更让人紧张了？抱歉，Boss，这是职业病。",note = "",illustrator_cv = "",class = 54,type = 0},
	[5302] = {id = 5302,name = "终幕双人舞",fit_gun = 289,voice = 19,substitute_voice = "",dialog = "落在火中的一刻，真心就会显露。开枪的时刻，生死也像这样决定……所以艺术和生死是以什么为纽带相通的吗，指挥官？",note = "",illustrator_cv = {"二花", "朝日奈丸佳"},class = 54,type = 1},
	[5303] = {id = 5303,name = "甜蜜许愿",fit_gun = 313,voice = 19,substitute_voice = "",dialog = "圣诞节这么开心，如果能每天都是圣诞节的话就好了！不过那样快乐就少了？那，那本小姐就勉为其难地妥协吧……",note = "",illustrator_cv = "",class = 54,type = 0},
	[5304] = {id = 5304,name = "燃心铁皮人",fit_gun = 250,voice = 19,substitute_voice = "",dialog = "能让指挥官的心率一直保持在开心状态的机会到底在哪里呢……我要找找看。",note = "",illustrator_cv = "",class = 54,type = 0},
	[5305] = {id = 5305,name = "朝暮仙子",fit_gun = 270,voice = 19,substitute_voice = "",dialog = "第四号叮叮仙子向您问安。即使这样的装扮只能维持一天，吾辈也会为您发光发热！",note = "",illustrator_cv = "",class = 54,type = 2},
	[5306] = {id = 5306,name = "拾愿者K德瑞拉",fit_gun = 212,voice = 19,substitute_voice = "",dialog = "在榛树的枝条下许愿，仙女就会降临……现在，您可以说出愿望了。",note = "",illustrator_cv = "",class = 54,type = 0},
	[5307] = {id = 5307,name = "女王的高塔派对",fit_gun = 319,voice = 19,substitute_voice = "",dialog = "这种大小的圣诞树，我用头发也能搬！",note = "",illustrator_cv = "",class = 54,type = 0},
	[5308] = {id = 5308,name = "女仆团的狂飙午夜",fit_gun = 95,voice = 19,substitute_voice = "",dialog = "欢迎回来，主人！您似乎对这新装备有所疑问呢！如果陪小爱度过一段夜空下的旅程，小爱就告诉您答案哦~",note = "",illustrator_cv = "",class = 54,type = 1},
	[5309] = {id = 5309,name = "胭雪公主",fit_gun = 5,voice = 19,substitute_voice = "",dialog = "小个子也有小个子的好处，礼物盒的大小刚刚好！您的礼物送到了哦，指挥官！",note = "",illustrator_cv = "",class = 54,type = 0},
	[5401] = {id = 5401,name = "蓝黎软香",fit_gun = 274,voice = 15,substitute_voice = "",dialog = "指挥官，你来啦？今天要去拜访大家呢。咦，已经拜访完了吗？",note = "",illustrator_cv = "",class = 55,type = 0},
	[5402] = {id = 5402,name = "繁花访客",fit_gun = 160,voice = 15,substitute_voice = "",dialog = "今天穿着漂亮衣服出门的美少女一定很多，<br>我已经迫不及待了！",note = "",illustrator_cv = "",class = 55,type = 1},
	[5403] = {id = 5403,name = "傲雪隽茶",fit_gun = 97,voice = 15,substitute_voice = "",dialog = "还在磨蹭什么？事情可多着呢。<br>到我身边来，我们一起去。",note = "",illustrator_cv = {"八号地球", "古賀葵"},class = 55,type = 0},
	[5404] = {id = 5404,name = "新诗谱写时",fit_gun = 150,voice = 15,substitute_voice = "",dialog = "看来今天时间正好，走吧，指挥官。传统可不是简单就能做到的，我带你去练习。",note = "",illustrator_cv = "",class = 55,type = 0},
	[5405] = {id = 5405,name = "侘寂旅人",fit_gun = 103,voice = 15,substitute_voice = "",dialog = "想不到指挥官您也在这里。这是巧合，对吧？<br>完全的巧合。所以别紧张，享受寂静。",note = "",illustrator_cv = {"K箱", "嶺内ともみ"},class = 55,type = 2},
	[5406] = {id = 5406,name = "冬原宅急便",fit_gun = 254,voice = 15,substitute_voice = "",dialog = "不好了，指挥官！你快看，这就是它们的一员！在最后，我还有话想对你……诶，只是普通的猫而已？",note = "",illustrator_cv = "",class = 55,type = 0},
	[5407] = {id = 5407,name = "春报信使",fit_gun = 230,voice = 15,substitute_voice = "",dialog = "这身衣服是租的吧，所以不用花很多钱……只是穿一天的话，这样比较省钱嘛。什么，是买下来的？指挥官买给我？谢谢您！",note = "",illustrator_cv = "",class = 55,type = 0},
	[5408] = {id = 5408,name = "红花头筹",fit_gun = 131,voice = 15,substitute_voice = "",dialog = "这是许愿的好时候呢，能和您一起做这件事真让人开心。",note = "",illustrator_cv = "",class = 55,type = 0},
	[5501] = {id = 5501,name = "迎寒客",fit_gun = 253,voice = 15,substitute_voice = "",dialog = "寻常窗前月，有梅便不同。指挥官，新年的第一天，来装点一下指挥室吧！",note = "",illustrator_cv = "",class = 56,type = 1},
	[5502] = {id = 5502,name = "舞凌霄",fit_gun = 261,voice = 15,substitute_voice = "",dialog = "祝指挥官新年快乐，喜气洋洋！红包请放在这边，谢谢啦！",note = "",illustrator_cv = "",class = 56,type = 2},
	[5503] = {id = 5503,name = "聚瑶芳",fit_gun = 315,voice = 15,substitute_voice = "",dialog = "前辈，小心——鞭炮要炸了哦！",note = "",illustrator_cv = "",class = 56,type = 0},
	[5504] = {id = 5504,name = "挽玉环",fit_gun = 252,voice = 15,substitute_voice = "",dialog = "新年会有新的挑战，指挥官，你又考虑到了多远的未来呢？",note = "",illustrator_cv = "",class = 56,type = 0},
	[5505] = {id = 5505,name = "梦倒仙",fit_gun = 302,voice = 15,substitute_voice = "",dialog = "啊，指挥官……抱歉，我只是想偷偷喝一杯……",note = "",illustrator_cv = "",class = 56,type = 0},
	[5506] = {id = 5506,name = "待月丹",fit_gun = 43,voice = 15,substitute_voice = "",dialog = "新年第一天，打起精神来啊。来，拿好你的红包。",note = "",illustrator_cv = {"柱柱", "田中美海"},class = 56,type = 0},
	[5507] = {id = 5507,name = "吻琼苞",fit_gun = 13,voice = 15,substitute_voice = "",dialog = "一年一次的春节，就让我们稍微奢侈一点吧。长官，我这样穿好看吗？",note = "",illustrator_cv = "",class = 56,type = 0},
	[5508] = {id = 5508,name = "扶绛梨",fit_gun = 49,voice = 15,substitute_voice = "",dialog = "新年开运，洪福齐天！长官，新年快乐！",note = "",illustrator_cv = "",class = 56,type = 0},
	[5601] = {id = 5601,name = "日落黄昏时",fit_gun = 294,voice = 16,substitute_voice = "",dialog = "很多年前，希波吕托斯在香桃树下面临了人生的重大考验，他最终无惧死亡选择了崇敬之人，换做是我的话，亦会作此抉择。",note = "",illustrator_cv = {"Firewood", "種﨑敦美"},class = 57,type = 0},
	[5602] = {id = 5602,name = "天意佳人",fit_gun = 119,voice = 16,substitute_voice = "",dialog = "还在等什么呢，指挥官？过来吧，别浪费只属于我们两个人的时间。",note = "",illustrator_cv = "",class = 57,type = 1},
	[5603] = {id = 5603,name = "第1000只纸鹤",fit_gun = 307,voice = 16,substitute_voice = "",dialog = "有人告诉我，用心折下一千只纸鹤就能为心爱的人带来幸福好运。等我折下这最后一只，指挥官就能得到幸福了。",note = "",illustrator_cv = "",class = 57,type = 1},
	[5604] = {id = 5604,name = "洋桔梗与她的季节",fit_gun = 129,voice = 16,substitute_voice = "",dialog = "这种花的花型与玫瑰十分相近，但却不带刺，这样即便我们紧紧拥抱，也不会不小心扎伤你了。",note = "",illustrator_cv = {"鳩山井鶴", "大門香実"},class = 57,type = 2},
	[5605] = {id = 5605,name = "和伊萨卡的漫长假日",fit_gun = 153,voice = 16,substitute_voice = "",dialog = "假日的第一天就不知道该做些什么了吗？那……不如与完美无缺、富有魅力的我一起虚度时光吧，指挥官。",note = "",illustrator_cv = {"哈奈鲁", "加藤英美里"},class = 57,type = 0},
	[5606] = {id = 5606,name = "玫瑰棋手的告白",fit_gun = 216,voice = 16,substitute_voice = "",dialog = "……哼，难得赢了一次就迫不及待高兴起来了吗？坦白告诉你，刚刚那一局是我让你的，不过能换来你的笑容也还算值当吧。",note = "",illustrator_cv = "",class = 57,type = 0},
	[5607] = {id = 5607,name = "鹿韭旅伴",fit_gun = 174,voice = 16,substitute_voice = "",dialog = "指挥官，迄今为止，我已经陪伴您出发了无数次……我知道，我们距离胜利的终点还很久远，但我会一直陪伴在您身边。",note = "",illustrator_cv = "",class = 57,type = 0},
	[5608] = {id = 5608,name = "雀闻弦歌",fit_gun = 94,voice = 16,substitute_voice = "",dialog = "听闻孔雀只要听见弦歌，就会情不自禁张开尾羽，展现自己最美的一面……就像我听见长官的声音一样。",note = "",illustrator_cv = "",class = 57,type = 0},
	[5701] = {id = 5701,name = "她来自比利时",fit_gun = 245,voice = 4,substitute_voice = "",dialog = "Bonjour//c Mon ami~你觉得我是法国人吗？指挥官？ 动动你小小的灰色细胞好好想想吧，是时候做点真正的工作了。",note = "",illustrator_cv = "",class = 58,type = 0},
	[5702] = {id = 5702,name = "一份不适合她的工作",fit_gun = 243,voice = 4,substitute_voice = "",dialog = "所有的漂亮女人都很坚强……要不然我们要怎么生存下去？",note = "",illustrator_cv = "",class = 58,type = 1},
	[5703] = {id = 5703,name = "无间般若",fit_gun = 16,voice = 4,substitute_voice = "",dialog = "赎罪？谁稀罕？犯了错就该付出代价，没有别的选择，这就是爱（正义）啊。",note = "",illustrator_cv = {"小枪", "種田梨沙"},class = 58,type = 2},
	[5704] = {id = 5704,name = "宿命的修护者",fit_gun = 317,voice = 4,substitute_voice = "",dialog = "难道我真的怀疑错了吗……不，证据是不会撒谎的。请允许我再做一次调查，指挥官。",note = "",illustrator_cv = "",class = 58,type = 0},
	[5706] = {id = 5706,name = "银色子弹",fit_gun = 262,voice = 4,substitute_voice = "",dialog = "侦探齐聚于此，真是不得了的奇迹——您期待我们分出高下吗？哈哈，推理可没有什么输赢，因为真相，只有一个。",note = "",illustrator_cv = "",class = 58,type = 0},
	[5707] = {id = 5707,name = "马盖尔小姐",fit_gun = 258,voice = 4,substitute_voice = "",dialog = "我太了解人类了，指挥官，你总是很轻易就相信别人，但我可不会。",note = "",illustrator_cv = "",class = 58,type = 0},
	[5801] = {id = 5801,name = "如蝶翩跹",fit_gun = 285,voice = 4,substitute_voice = "",dialog = "锵锵锵——周年限定版的C93华丽登场！让全场的闪光灯为我闪耀得更猛烈些吧！",note = "",illustrator_cv = "",class = 61,type = 0},
	[5802] = {id = 5802,name = "香槟怪盗",fit_gun = 260,voice = 4,substitute_voice = "",dialog = "指挥官，敢不敢和我赌一把？就赌，在你喝完这瓶香槟之前，我能不能偷走你的心……",note = "",illustrator_cv = "",class = 61,type = 1},
	[5803] = {id = 5803,name = "来自闇冥",fit_gun = 231,voice = 4,substitute_voice = "",dialog = "就像从花叶上滑落的血滴消失在黑暗中一样，无法释怀的仇怨，由我为您消解。",note = "",illustrator_cv = "",class = 61,type = 0},
	[5804] = {id = 5804,name = "银湾流转夜",fit_gun = 312,voice = 4,substitute_voice = "",dialog = "听说星光要走很远的路才能抵达地球，并被我们所看见。 所以，今晚这样璀璨的星河，更值得被我们铭记。",note = "",illustrator_cv = "",class = 61,type = 0},
	[5805] = {id = 5805,name = "花下踟躇醉",fit_gun = 161,voice = 4,substitute_voice = "",dialog = "嘘——长官，这酒不得劲，还是老白干爽利。我们趁现在一起偷偷溜进仓库，喝个痛快怎么样？",note = "",illustrator_cv = {"豆芽", "大坪由佳"},class = 61,type = 1},
	[5806] = {id = 5806,name = "她从雨中来",fit_gun = 330,voice = 4,substitute_voice = "",dialog = "听说在雨天共撑一把伞走过这条小巷的话，两个灵魂就会永不分离……啊呀，骗你的啦。",note = "",illustrator_cv = "",class = 61,type = 0},
	[5807] = {id = 5807,name = "金色回忆",fit_gun = 108,voice = 4,substitute_voice = "",dialog = "能和我跳一支舞吗，指挥官？难得盛装出席，我希望能留下一些只属于我和你的记忆……",note = "",illustrator_cv = "",class = 61,type = 0},
	[5808] = {id = 5808,name = "气球的彼端",fit_gun = 21,voice = 4,substitute_voice = "",dialog = "气球之所以能够无所畏惧地肆意飞舞，一定是因为另一端掌握在信任之人的手中吧。这种安全感，我也很清楚。",note = "",illustrator_cv = "",class = 61,type = 0},
	[5901] = {id = 5901,name = "小小志愿者",fit_gun = 339,voice = 4,substitute_voice = "RPK203_0",dialog = "请各位小朋友乖乖排队，有序领取气球！啊，我也是小朋友……",note = "",illustrator_cv = "",class = 62,type = 1},
	[5902] = {id = 5902,name = "后卫新星",fit_gun = 311,voice = 4,substitute_voice = "Lusa_0",dialog = "见习队员卢萨，报到！最后的阵线就交给我吧！",note = "",illustrator_cv = "",class = 62,type = 0},
	[5903] = {id = 5903,name = "追风魔女",fit_gun = 313,voice = 4,substitute_voice = "SACR_0",dialog = "太慢了！竟敢让本小姐等到自己来找你，真是好大的胆子！",note = "",illustrator_cv = "",class = 62,type = 0},
	[5904] = {id = 5904,name = "雨后冲锋队",fit_gun = 173,voice = 4,substitute_voice = "PKP_0",dialog = "今天的出行会顺利的，因为有我在。",note = "",illustrator_cv = "",class = 62,type = 0},
	[5905] = {id = 5905,name = "火锅国国王",fit_gun = 295,voice = 4,substitute_voice = "06TypeSMG_0",dialog = "哼哼，闪亮的美食家登场了！辣椒们跟我冲啊，打败所有三鲜锅！",note = "",illustrator_cv = "",class = 62,type = 0},
	[5906] = {id = 5906,name = "接引彩虹的花仙子",fit_gun = 187,voice = 4,substitute_voice = "Ak5_0",dialog = "只要照顾好这些花，愿望就都会实现……指挥官，花会开出打折券的。",note = "",illustrator_cv = "",class = 62,type = 0},
	[5907] = {id = 5907,name = "猫咪的合奏",fit_gun = 297,voice = 4,substitute_voice = "M82_0",dialog = "猫猫们到底想说什么呢，要是不能赶快理解的话，回家就要晚了……",note = "",illustrator_cv = "",class = 62,type = 0},
	[5908] = {id = 5908,name = "甜蜜幻梦使",fit_gun = 182,voice = 4,substitute_voice = "wz29_0",dialog = "这是哪里？为什么我刚出家门就到这儿了……你需要帮助吗？",note = "",illustrator_cv = "",class = 62,type = 0},
	[6101] = {id = 6101,name = "夜潮的邀约",fit_gun = 323,voice = 4,substitute_voice = "",dialog = "能不能牵手……不，没什么，我只是在想你为什么没跟上来。",note = "",illustrator_cv = "",class = 63,type = 0},
	[6102] = {id = 6102,name = "熙光寓言",fit_gun = 282,voice = 4,substitute_voice = "",dialog = "在海边总会想到一些童话呢……我们边走边说吧。",note = "",illustrator_cv = "",class = 63,type = 1},
	[6103] = {id = 6103,name = "临海准则",fit_gun = 233,voice = 4,substitute_voice = "",dialog = "海边让人难以放松啊……不过对常人而言，这里应该是休假的地方吧。",note = "",illustrator_cv = "",class = 63,type = 0},
	[6105] = {id = 6105,name = "樱桃色珊瑚",fit_gun = 336,voice = 4,substitute_voice = "",dialog = "不是只有花里胡哨才能体现品味，简洁一样是美。",note = "",illustrator_cv = "",class = 63,type = 0},
	[6106] = {id = 6106,name = "酸甜水物语",fit_gun = 254,voice = 4,substitute_voice = "",dialog = "我的母星上有很多居民怕水，如果不是这样，我恐怕没法逃到地球来……海边真是安全的地方。",note = "",illustrator_cv = "",class = 63,type = 0},
	[6107] = {id = 6107,name = "飞翔海星",fit_gun = 325,voice = 4,substitute_voice = "",dialog = "这、这样下水会会会……会不会有问题啊……真的没问题吗？我不会进水吗？……呜哇！",note = "",illustrator_cv = "",class = 63,type = 0},
	[6108] = {id = 6108,name = "浮水薄荷茶",fit_gun = 322,voice = 4,substitute_voice = "",dialog = "老板！这里的饮料好喝，我给你拿了一杯薄荷茶，尝尝看！不够劲的话还有酒，免费的，我们喝够本再走！",note = "",illustrator_cv = "",class = 63,type = 0},
	[6109] = {id = 6109,name = "恋如树影",fit_gun = 10,voice = 4,substitute_voice = "",dialog = "还在等什么呢，指挥官？要我教你怎么做吗？你现在应该像宠物一样跑着靠近我哦。",note = "",illustrator_cv = {"柱柱", "伊藤あすか"},class = 63,type = 0},
	[6201] = {id = 6201,name = "不败的金色龙卷",fit_gun = 259,voice = 4,substitute_voice = "",dialog = "哟！指挥官，要来地下拳馆过两招吗？我的爪子今天可是状态绝佳啊！放心吧，就算你住院了我也会好好照顾你到出院的！",note = "",illustrator_cv = {"八号地球", "大地葉"},class = 65,type = 0},
	[6202] = {id = 6202,name = "郊狼蓝调",fit_gun = 337,voice = 4,substitute_voice = "",dialog = "唔？指挥官你刚刚有叫我？正好，帮我拿一下袋子，作为回报我可以借你一边耳机，不用花钱就能听我的CD，这可是稳赚不赔的好事。",note = "",illustrator_cv = "",class = 65,type = 1},
	[6203] = {id = 6203,name = "食劫者的正义",fit_gun = 226,voice = 4,substitute_voice = "",dialog = "指挥官，加快脚步！贫民窟的孩子们可都等着吃饭——什么？！你说袋子漏了？这种事情你怎么不早说啊！",note = "",illustrator_cv = {"豆芽", "市ノ瀬加那"},class = 65,type = 0},
	[6204] = {id = 6204,name = "忠友同游",fit_gun = 118,voice = 4,substitute_voice = "",dialog = "虽然这里充满了不安分的要素，但我会一直在您身旁的，所以，指挥官，请好好地看着我，不要离开我的身边哦。",note = "",illustrator_cv = "",class = 65,type = 0},
	[6205] = {id = 6205,name = "深巷的落雪",fit_gun = 6,voice = 4,substitute_voice = "",dialog = "指挥官，乱摸淑女的尾巴可不是绅士所为。唔，你是专程来这里见我的吗？没办法，就让你再多摸一分钟吧，下不为例哦。",note = "",illustrator_cv = "",class = 65,type = 0},
	[6206] = {id = 6206,name = "三只小猪来租房",fit_gun = 305,voice = 4,substitute_voice = "",dialog = "终于租到布局完全对称的房间啦！指挥官，帮我放一下行李吧，在把新家布置成完美的对称空间之前，我可得好好睡一觉。",note = "",illustrator_cv = "",class = 65,type = 0},
	[6207] = {id = 6207,name = "深郊遇白兔",fit_gun = 145,voice = 4,substitute_voice = "",dialog = "指挥官，今天天气不错哦，要和我一起去树林里野餐吗？虽然只有胡萝卜就是啦。",note = "",illustrator_cv = "",class = 65,type = 0},
	[6301] = {id = 6301,name = "溯溪踏秋时",fit_gun = 315,voice = 4,substitute_voice = "",dialog = "看这边，前辈，您也笑一笑啊！秋天可是最好的季节，鱼儿和螃蟹都肥美得让人开心！",note = "",illustrator_cv = "",class = 66,type = 0},
	[6302] = {id = 6302,name = "在世界的一角等你",fit_gun = 296,voice = 4,substitute_voice = "",dialog = "终于来了，指挥官！不，等你也没关系哦，因为在这段等待的时间里，我的心情可是充满了欢欣和期待。",note = "",illustrator_cv = "",class = 66,type = 1},
	[6303] = {id = 6303,name = "星河中沉睡的梦",fit_gun = 341,voice = 4,substitute_voice = "",dialog = "平常总是有太多的声音涌入耳朵：警报声、刹车声、按铃声……能像现在这样静静感受星空呼吸的时刻太少了。",note = "",illustrator_cv = "",class = 66,type = 0},
	[6304] = {id = 6304,name = "残雨暝烟",fit_gun = 317,voice = 4,substitute_voice = "",dialog = "我穿的太过严肃了吗，长官？毕竟就算是约会，我也需要保证您的安全。走吧，定好的餐厅快要迟到了。",note = "",illustrator_cv = "",class = 66,type = 0},
	[6305] = {id = 6305,name = "怦动三部曲",fit_gun = 310,voice = 4,substitute_voice = "",dialog = "脸庞、嘴唇、气味……都要仔细确认无误才可以出击。没错哦，长官，只有完美的准备才能开启完美的约会。",note = "",illustrator_cv = "",class = 66,type = 0},
	[6306] = {id = 6306,name = "向着碧空漾去",fit_gun = 223,voice = 4,substitute_voice = "",dialog = "坐在秋千上荡入空中时，就好像即将逃离重力一样……指挥官，要来感受一下吗？",note = "",illustrator_cv = "",class = 66,type = 0},
	[6401] = {id = 6401,name = "素白濯清涟",fit_gun = 129,voice = 18,substitute_voice = "",dialog = "传说有一位白娘子，行走人间，却能不染上分毫的污浊，就像这荷花一样。不知我是否也可做到这般的清白无邪……",note = "",illustrator_cv = {"鳩山井鶴", "大門香実"},class = 67,type = 1},
	[6402] = {id = 6402,name = "猩红夜猎手",fit_gun = 329,voice = 18,substitute_voice = "",dialog = "狩猎游戏将从今夜开始，直到黎明到来为止。而在这之前，我绝不会倒下。",note = "",illustrator_cv = "",class = 67,type = 0},
	[6403] = {id = 6403,name = "献给乔安娜",fit_gun = 213,voice = 18,substitute_voice = "",dialog = "想要做我的部下，就乖乖把心尖的第一滴血和美味食物的第一口全部献给我吧……",note = "",illustrator_cv = "",class = 67,type = 0},
	[6404] = {id = 6404,name = "偷心魔不眠",fit_gun = 286,voice = 18,substitute_voice = "",dialog = "Honey——我可以用这颗糖换走你的心吗？不行的话，我就要强来了喔……",note = "",illustrator_cv = "",class = 67,type = 0},
	[6405] = {id = 6405,name = "迷途引渡人",fit_gun = 220,voice = 18,substitute_voice = "",dialog = "迷途的羔羊或许不该就这样被送回拥挤的羊圈，来到我温暖的收藏柜里才是更好的选择。",note = "",illustrator_cv = "",class = 67,type = 0},
	[6406] = {id = 6406,name = "拼凑者的独白",fit_gun = 81,voice = 18,substitute_voice = "",dialog = "请别害怕，我只是想感受下那位科学怪人的心情，被整个世界仇恨背叛究竟是什么样的体验……",note = "",illustrator_cv = "",class = 67,type = 0},
	[6501] = {id = 6501,name = "苍穹的迦楼罗",fit_gun = 272,voice = 4,substitute_voice = "",dialog = "在这寻求未来的舞台上，我将倾尽全力扮演好自己的角色，而您……一定能够终结一切战争，对吧？指挥官。",note = "",illustrator_cv = "",class = 69,type = 0},
	[6502] = {id = 6502,name = "真红陨星",fit_gun = 338,voice = 4,substitute_voice = "",dialog = "哟，指挥官，要来我的专属机体上坐坐吗？不管是飞行速度还是在上面打牌的舒适度，都是普通的机体的三倍哟~",note = "",illustrator_cv = "",class = 69,type = 0},
	[6503] = {id = 6503,name = "纯白流星负愿而行",fit_gun = 95,voice = 4,substitute_voice = "",dialog = "贵安，主人，HY-088驾驶员“纯白流星”小爱前来报到，我愿背负怪物的污名，只为带大家走上您所承诺那条结束所有战争的路。",note = "",illustrator_cv = "",class = 69,type = 1},
	[6504] = {id = 6504,name = "苍龙屹立09区之上",fit_gun = 263,voice = 4,substitute_voice = "",dialog = "人机同步率已达百分之九十，苍龙号驾驶员MG36随时可以接受命令，鉴于这次行动难度较高，下班后的电影票就由长官您来买单了。",note = "",illustrator_cv = "",class = 69,type = 0},
	[6505] = {id = 6505,name = "子夜福音",fit_gun = 65,voice = 4,substitute_voice = "",dialog = "如果你有实现那个单纯幼稚的愿望的可行方案，指挥官，我倒是不介意为你献出……子夜！我话还没说完呢！别去乱蹭那个家伙啊！",note = "",illustrator_cv = "",class = 69,type = 0},
	[6507] = {id = 6507,name = "荒漠的龙与诗",fit_gun = 178,voice = 4,substitute_voice = "",dialog = "“黄沙飞扬，钢铁的龙奔行于荒漠之上。”指挥官，要和我一起骑着咬咬兜风吗？",note = "",illustrator_cv = "",class = 69,type = 0},
	[6601] = {id = 6601,name = "麋鹿宣言",fit_gun = 290,voice = 19,substitute_voice = "",dialog = "分发礼物一定要跟圣诞老人一起吗？我觉得用上我新发明的礼物喷射机器，效率完全没问题啊！",note = "",illustrator_cv = "",class = 70,type = 1},
	[6603] = {id = 6603,name = "狩猎时刻",fit_gun = 4,voice = 19,substitute_voice = "",dialog = "在这里坐了一晚上，终于看见了一个不错的‘猎物’……一起喝杯酒吗，指挥官？",note = "",illustrator_cv = {"八号地球", "たかはし智秋"},class = 70,type = 0},
	[6604] = {id = 6604,name = "与帕罗莎同座",fit_gun = 333,voice = 19,substitute_voice = "",dialog = "这样的温度下冬泳果然非常消耗体力，你也累了吧，指挥官，我身边的位置是留给你的。",note = "",illustrator_cv = "",class = 70,type = 0},
	[6605] = {id = 6605,name = "浪巅决骤",fit_gun = 288,voice = 19,substitute_voice = "",dialog = "指挥官，来一起冲浪吧！很简单的，这样抓浪、顺势乘上去就行，就像海上漫步一样哦……",note = "",illustrator_cv = "",class = 70,type = 0},
	[6606] = {id = 6606,name = "泳池边的圣诞奇遇",fit_gun = 225,voice = 19,substitute_voice = "",dialog = "圣诞快乐，指挥官，我和这届的冬泳冠军一起来给您送礼物啦！诶，企鹅不可以参赛吗……",note = "",illustrator_cv = "",class = 70,type = 0},
	[6607] = {id = 6607,name = "暖冬速递员",fit_gun = 304,voice = 19,substitute_voice = "",dialog = "有你的快递，指挥官！不是那边啦，你的礼物是我的拥抱！",note = "",illustrator_cv = "",class = 70,type = 0},
	[6608] = {id = 6608,name = "圣夜精灵高高挂",fit_gun = 70,voice = 19,substitute_voice = "",dialog = "救命啊，指挥官！因为偷吃光了仓库的巧克力，我被挂起来当圣诞吉祥物了！你、你怎么笑得这么大声……",note = "",illustrator_cv = "",class = 70,type = 0},
	[6701] = {id = 6701,name = "携枝踏玉尘",fit_gun = 20,voice = 15,substitute_voice = "",dialog = "指挥官，大雪天出游怎么没有带伞？算了，我这把给你用……哦？你说一起用？那就这样好了。",note = "",illustrator_cv = {"RIRE", "早見沙織"},class = 71,type = 1},
	[6702] = {id = 6702,name = "巫姬翩舞起",fit_gun = 323,voice = 15,substitute_voice = "",dialog = "指挥官，请不要一直盯着我，这是为了今年的神乐舞而准备的着装……你说想看一看？真拿你没办法啊，就一小段哦。",note = "",illustrator_cv = "",class = 71,type = 0},
	[6703] = {id = 6703,name = "竹剑扫落雪",fit_gun = 311,voice = 15,substitute_voice = "",dialog = "指挥官，新年伊始，要不要试试足球之外的新运动？比如……剑道？",note = "",illustrator_cv = "",class = 71,type = 0},
	[6704] = {id = 6704,name = "幽玄旅人",fit_gun = 101,voice = 15,substitute_voice = "",dialog = "指挥官！快来看看，明明是冬天，这里居然能长出这么高的草！这应该就是45姐说的幽玄之美吧！",note = "",illustrator_cv = {"GINGALA", "能登麻美子"},class = 71,type = 0},
	[6705] = {id = 6705,name = "三友伴岁寒",fit_gun = 328,voice = 15,substitute_voice = "",dialog = "竹子的位置要不要再——啊，是指挥官啊，来的正好，你觉得这个“岁寒三友”还有什么要调整的？哈？才不是随便摆摆就行，插花和侍弄枪械一样，可是有大学问的！",note = "",illustrator_cv = "",class = 71,type = 0},
	[6706] = {id = 6706,name = "初梦见飞羽",fit_gun = 325,voice = 15,substitute_voice = "",dialog = "哈唔——指挥官，今，今年的初梦有没有梦到富士山呀？诶，我，我吗？我只梦到了板羽球……",note = "",illustrator_cv = "",class = 71,type = 0},
	[6707] = {id = 6707,name = "幼虎祈丰年",fit_gun = 37,voice = 15,substitute_voice = "",dialog = "指挥官，听说把绘马绑在树上，愿望实现的可能性会更高呢！我们可以帮你把它射到那棵松树上哦！",note = "",illustrator_cv = {"哈奈鲁", "大西沙織"},class = 71,type = 0},
	[6801] = {id = 6801,name = "寒兰傲",fit_gun = 341,voice = 4,substitute_voice = "",dialog = "除夕朔望，寒兰映雪。指挥官，新春之夜虽无月色，但格里芬的大家于此共享佳节的团圆之刻，已是更胜满月的美景了。",note = "",illustrator_cv = "",class = 72,type = 1},
	[6802] = {id = 6802,name = "夕颜展",fit_gun = 331,voice = 4,substitute_voice = "",dialog = "新年快乐！您似乎看起来没什么精神啊，那就来一口蜂鸟珍藏了一年的红色可乐葫芦限量版吧！香浓醇厚又提神哦！",note = "",illustrator_cv = "",class = 72,type = 0},
	[6803] = {id = 6803,name = "醉茗咲",fit_gun = 356,voice = 4,substitute_voice = "",dialog = "来，指挥官，我们一起干了这瓶二锅头！看看新的一年里，你的酒量是否和你的实力一样长进飞快！",note = "",illustrator_cv = "",class = 72,type = 0},
	[6804] = {id = 6804,name = "朱砂舞",fit_gun = 340,voice = 4,substitute_voice = "",dialog = "哈哈！新年的创作氛围就是自由啊！来，指挥官，让我给你在办公室墙上来一幅国画风涂鸦！",note = "",illustrator_cv = "",class = 72,type = 0},
	[6805] = {id = 6805,name = "矶松叹",fit_gun = 348,voice = 4,substitute_voice = "",dialog = "长官，新的一年也请多多指教……您问身上的打扮？经过我的观察，这种服装肯定是一种格里芬的新年习俗，我也要快速融入才好！",note = "",illustrator_cv = "",class = 72,type = 0},
	[6806] = {id = 6806,name = "天星落",fit_gun = 28,voice = 4,substitute_voice = "",dialog = "哟！“饲养员”，我正想去找你呢！谢谢你这一年对本“天才”的照顾啦，在这些红包里选一个吧，说不定会有意想不到的大奖哦。",note = "",illustrator_cv = "",class = 72,type = 0},
	[6807] = {id = 6807,name = "玉瓯立",fit_gun = 64,voice = 4,substitute_voice = "",dialog = "落日桐阴转，微风栀子香。长官，希望您会喜欢我的新年礼物——这风中轻唱的玉瓯之歌。",note = "",illustrator_cv = "",class = 72,type = 0},
	[6808] = {id = 6808,name = "桃夭灼",fit_gun = 7,voice = 4,substitute_voice = "",dialog = "恭喜发财，红包拿来~嘻嘻，今天倒是很爽快嘛，我亲爱的长官。",note = "",illustrator_cv = {"_椿日_", "喜多村英梨"},class = 72,type = 0},
	[6809] = {id = 6809,name = "蜀锦羞",fit_gun = 51,voice = 4,substitute_voice = "",dialog = "指、指挥官，今晚的年夜饭是我亲手准备的哦，如果您能喜欢就好了……",note = "",illustrator_cv = "",class = 72,type = 0},
	[6901] = {id = 6901,name = "紫鸢醉蝶",fit_gun = 253,voice = 16,substitute_voice = "",dialog = "指挥官，真的不考虑和美少女喝一杯吗？这可是我精心挑选的高颜值葡萄酒哦……放心放心，就算醉了，我也不会对您做什么的啦~哼♪",note = "",illustrator_cv = "",class = 73,type = 1},
	[6902] = {id = 6902,name = "风中祝祷",fit_gun = 130,voice = 16,substitute_voice = "",dialog = "唔……好大的风，我的愿望说到哪儿了……咦？长，长官？！您都听见了？！……是很普通的心愿吧？你会笑话我吗？",note = "",illustrator_cv = {"Nagu", "佐倉綾音"},class = 73,type = 0},
	[6903] = {id = 6903,name = "幻想圆舞曲",fit_gun = 242,voice = 16,substitute_voice = "",dialog = "这西装看起来真不坏……彼此彼此，礼服也很美……失陪一下！指挥官，请赏光和我跳一支——不，两支舞吧！男士和女士的舞步都不可以懈怠哦。",note = "",illustrator_cv = {"二花", "田所あずさ"},class = 73,type = 0},
	[6904] = {id = 6904,name = "于尘埃间仰望",fit_gun = 127,voice = 16,substitute_voice = "",dialog = "抱歉，打扰您了。我只是想告诉长官，无论您同意与否，我都会追随您的脚步，直到尽头。",note = "",illustrator_cv = "",class = 73,type = 0},
	[6905] = {id = 6905,name = "维纳斯之阵",fit_gun = 212,voice = 16,substitute_voice = "",dialog = "命运之轮预示我们在此等待，巨网撒下之时，相爱的恋人注定难解难分……指挥官，您知道这句占卜词的含义吗？",note = "",illustrator_cv = "",class = 73,type = 0},
	[6906] = {id = 6906,name = "彼岸星光",fit_gun = 218,voice = 16,substitute_voice = "",dialog = "就算不看我的眼神，您也能从动作中读出我现在的想法吧？那么……您意下如何呢……",note = "",illustrator_cv = "",class = 73,type = 0},
	[6907] = {id = 6907,name = "冬柏来信",fit_gun = 34,voice = 16,substitute_voice = "",dialog = "在抵达这一刻之前，我已经准备了一整个冬天。现在，这是只属于我和您，两个人的时间。",note = "",illustrator_cv = {"Alize", "種田梨沙"},class = 73,type = 0},
	[6908] = {id = 6908,name = "晨露之歌",fit_gun = 256,voice = 16,substitute_voice = "",dialog = "小羊羔，为什么喜欢的感觉总是伴随着忧愁和苦恼呢……你也和我有着同样的忧愁吗？",note = "",illustrator_cv = "",class = 73,type = 0},
	[6909] = {id = 6909,name = "骑士与守护者",fit_gun = 81,voice = 16,substitute_voice = "",dialog = "我没有什么可以赠与您的——除却一颗真心。从今以后，请让我牵起您的手，保护您，为您献出全部的生命。",note = "",illustrator_cv = "",class = 73,type = 0},
	[7001] = {id = 7001,name = "CZ75不加班",fit_gun = 166,voice = 4,substitute_voice = "",dialog = "喏，巧克力……吃了就拿出干劲！三点前我要看到需求反馈！还有这堆文件明天我要见到在我桌上——做不完就给我自愿加班，干活！",note = "",illustrator_cv = {"--", "古城門志帆"},class = 74,type = 0},
	[7002] = {id = 7002,name = "飞向云端",fit_gun = 330,voice = 4,substitute_voice = "",dialog = "嗯，好的老板，已经列入待办事项了老板，保证最优先处理……机场信号不好，回头再跟您联系——好！假装出差的休假日，最赞了！",note = "",illustrator_cv = "",class = 74,type = 0},
	[7003] = {id = 7003,name = "深夜暖茶",fit_gun = 310,voice = 4,substitute_voice = "",dialog = "我爱工作，我爱公司……只要做完这些就可以休息了，一起努力吧，前辈，我泡了姜茶……前辈？前辈！？请不要丢下我一个人下班啊！",note = "",illustrator_cv = "",class = 74,type = 0},
	[7004] = {id = 7004,name = "新入职员",fit_gun = 308,voice = 4,substitute_voice = "",dialog = "终于拿到offer啦，第一天上班一定要给公司的前辈们留下一个好印象……嗯？楼里刚才是不是有人在惨叫，是我的错觉吗……？",note = "",illustrator_cv = "",class = 74,type = 0},
	[7101] = {id = 7101,name = "暗蔷摇曳",fit_gun = 307,voice = 4,substitute_voice = "",dialog = "您在找我吗？我不打算中途离席哦，别担心，只是有些热，用酒降降温……您不会怪我把地毯弄脏吧？",note = "",illustrator_cv = "",class = 75,type = 1},
	[7102] = {id = 7102,name = "蝶影绮谭",fit_gun = 228,voice = 4,substitute_voice = "",dialog = "红蝴蝶……现在很少见了吧。我家乡有个说法，红蝴蝶会在梦里幻化成心上人的模样……唔，我当然是真实的呀？",note = "",illustrator_cv = "",class = 75,type = 0},
	[7103] = {id = 7103,name = "孤月之冠",fit_gun = 357,voice = 4,substitute_voice = "",dialog = "我听说您约我在这见面……被忽悠了吗？可是，您真的来了……等等，这两杯酒放了一夜，还是重新倒吧。",note = "",illustrator_cv = "",class = 75,type = 0},
	[7104] = {id = 7104,name = "星尘帷幕",fit_gun = 302,voice = 4,substitute_voice = "",dialog = "帷幕一旦拉开，失控的情感就将倾泻而出……即便如此，您还是想靠近我吗？",note = "",illustrator_cv = "",class = 75,type = 0},
	[7105] = {id = 7105,name = "碎金花信",fit_gun = 220,voice = 4,substitute_voice = "",dialog = "看您都挪不开眼了，我的庭院很美吧？这条用石头刻成的银河……足够让您心甘情愿地沉溺吗？",note = "",illustrator_cv = "",class = 75,type = 0},
	[7106] = {id = 7106,name = "爱神降临夜",fit_gun = 95,voice = 4,substitute_voice = "",dialog = "向我祈祷吧，命运指引我来此实现你的愿望……我在为演出排练啦，主人！不过，您许的愿望……不是哄小爱的吧？",note = "",illustrator_cv = "",class = 75,type = 1},
	[7107] = {id = 7107,name = "星云忌廉",fit_gun = 5,voice = 4,substitute_voice = "",dialog = "今晚的云朵厚厚软软的，超像一个巨大的奶油蛋糕！……诶，以我的个子也尝不到？不要戳破嘛！",note = "",illustrator_cv = "",class = 75,type = 0},
	[7108] = {id = 7108,name = "啜风吟",fit_gun = 94,voice = 4,substitute_voice = "",dialog = "长官，到我这儿来躲酒了吗？……小心哦，虽然看起来只有龙井，但这儿的夜风也会醉人的。",note = "",illustrator_cv = "",class = 75,type = 0},
	[7201] = {id = 7201,name = "骄阳般起舞",fit_gun = 312,voice = 4,substitute_voice = "VSK_0",dialog = "加油！加油！Fight ON！呼……这是我为孩子们设计的拉拉队舞，您觉得怎么样？有燃烧起来的感觉吗！？",note = "",illustrator_cv = "",class = 76,type = 0},
	[7202] = {id = 7202,name = "春杪梦鸢人",fit_gun = 129,voice = 4,substitute_voice = "95type_0",dialog = "长官长官，昨晚我梦见自己变成纸鸢飞起来了哦！……嘿嘿，没什么好怕的，因为您会握紧线的另一端嘛。",note = "",illustrator_cv = {"鳩山井鶴", "大門香実"},class = 76,type = 1},
	[7203] = {id = 7203,name = "冬眠守梦者",fit_gun = 233,voice = 4,substitute_voice = "Px4Storm_0",dialog = "喂喂，这里是我的地盘，我的几个小弟正在冬眠，闲人禁止进入……唔，看你一副超想进来玩的样子，那勉为其难——交个入场费就放你进来。",note = "",illustrator_cv = "",class = 76,type = 0},
	[7204] = {id = 7204,name = "训宠员日常",fit_gun = 305,voice = 4,substitute_voice = "Tabuk_0",dialog = "等、等一下，不可以咬绳子，快点还给我……啊，你们不要跑到同一边，这样会破坏对称的！呜呜，指挥官，救救我……！",note = "",illustrator_cv = "",class = 76,type = 0},
	[7205] = {id = 7205,name = "未来构想家",fit_gun = 88,voice = 4,substitute_voice = "MG3_0",dialog = "指挥官，这是我设计的多功能万向车，有了它，以后您就能把随身的东西放在上面，解放双手了，我来给您演示一下……咦？怎、怎么冒烟了！？",note = "",illustrator_cv = "",class = 76,type = 0},
	[7206] = {id = 7206,name = "熊耳剪裁师",fit_gun = 49,voice = 4,substitute_voice = "56typeR_0",dialog = "像这样剪好熊猫的耳朵，再戴到头上，就能成为熊猫的朋友了！指挥官，我也给你做了一个……",note = "",illustrator_cv = "",class = 76,type = 0}
}
return data